// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";

import {PrimeAuth} from "../auth/PrimeAuth.sol";

contract BoringVault is ERC20, PrimeAuth, ERC721Holder, ERC1155Holder {
    using Address for address;
    using SafeTransferLib for ERC20;

    /* ========================================= STATE ========================================= */

    /** @notice Underlying asset token (e.g., USDC, WETH) */
    ERC20 public immutable asset;

    /** @notice Optional hook called before balance changes (mint/burn/transfer) */
    IBeforeUpdateHook public beforeUpdateHook;

    /* ========================================= EVENTS ========================================= */

    event Enter(address indexed from, address indexed asset, uint256 amount, address indexed to, uint256 shares);
    event Exit(address indexed to, address indexed asset, uint256 amount, address indexed from, uint256 shares);

    /* ========================================= ERRORS ========================================= */

    error BoringVault__InvalidAsset();

    /* ========================================= CONSTRUCTOR ========================================= */

    /**
     * @notice Initialize vault with asset and access control
     * @param _primeRBAC PrimeRBAC contract for protocol-level roles
     * @param _authority RolesAuthority for vault-specific RBAC
     * @param _name Vault share token name
     * @param _symbol Vault share token symbol
     * @param _asset Underlying asset address
     */
    constructor(
        address _primeRBAC,
        address _authority,
        string memory _name,
        string memory _symbol,
        address _asset
    ) ERC20(_name, _symbol, ERC20(_asset).decimals()) PrimeAuth(_primeRBAC, _authority) {
        asset = ERC20(_asset);
    }

    /* ========================================= MANAGE ========================================= */

    /**
     * @notice Execute arbitrary call to DeFi protocols (e.g., Aave deposit, Uniswap swap)
     * @dev Restricted to MANAGER_ROLE. Must be validated by decoder/sanitizer
     * @param target Contract address to call
     * @param data Encoded function call
     * @param value ETH amount to send
     * @return result Return data from call
     */
    function manage(address target, bytes calldata data, uint256 value) external requiresAuth returns (bytes memory result) {
        result = target.functionCallWithValue(data, value);
    }

    /**
     * @notice Execute multiple calls atomically
     * @dev Restricted to MANAGER_ROLE. All calls must succeed or entire transaction reverts
     * @param targets Array of contract addresses
     * @param data Array of encoded function calls
     * @param values Array of ETH amounts
     * @return results Array of return data
     */
    function bulkManage(address[] calldata targets, bytes[] calldata data, uint256[] calldata values) external requiresAuth returns (bytes[] memory results) {
        uint256 targetsLength = targets.length;
        results = new bytes[](targetsLength);
        for (uint256 i; i < targetsLength; ++i) {
            results[i] = targets[i].functionCallWithValue(data[i], values[i]);
        }
    }

    /* ========================================= DEPOSIT ========================================= */

    /**
     * @notice Allows minter to mint shares, in exchange for assets.
     * @dev If assetAmount is zero, no assets are transferred in.
     * @dev Callable by MINTER_ROLE.
     */
    function enter(address from, uint256 assetAmount, address to, uint256 shareAmount) external requiresAuth {
        // Transfer assets in
        if (assetAmount > 0) asset.safeTransferFrom(from, address(this), assetAmount);

        // Update rewards BEFORE minting (with old balances)
        _callBeforeUpdate(address(0), to, shareAmount, msg.sender);

        // Mint shares.
        _mint(to, shareAmount);

        emit Enter(from, address(asset), assetAmount, to, shareAmount);
    }

    /* ========================================= WITHDRAWAL ========================================= */

    /**
     * @notice Allows burner to burn shares, in exchange for assets.
     * @dev If assetAmount is zero, no assets are transferred out.
     * @dev Callable by BURNER_ROLE.
     */
    function exit(address to, uint256 assetAmount, address from, uint256 shareAmount) external requiresAuth {
        // Update rewards BEFORE burning (with old balances)
        _callBeforeUpdate(from, address(0), shareAmount, msg.sender);

        // Burn shares.
        _burn(from, shareAmount);

        // Transfer assets out.
        if (assetAmount > 0) asset.safeTransfer(to, assetAmount);

        emit Exit(to, address(asset), assetAmount, from, shareAmount);
    }

    /* ========================================= HOOKS ========================================= */

    /**
     * @notice Configure hook called before balance changes (optional)
     * @dev Restricted to PROTOCOL_ADMIN_ROLE. Set to address(0) to disable
     * @dev Common use case: Distributor contract for reward tracking
     * @param _hook Address implementing IBeforeUpdateHook interface
     */
    function setBeforeUpdateHook(address _hook) external onlyOwner {
        beforeUpdateHook = IBeforeUpdateHook(_hook);
    }

    /**
     * @notice Internal: Invoke hook before balance changes (if configured)
     * @dev Hook failure causes transaction revert
     * @param from Source address (address(0) for minting)
     * @param to Destination address (address(0) for burning)
     * @param amount Share amount being transferred
     * @param operator Transaction initiator (msg.sender)
     */
    function _callBeforeUpdate(address from, address to, uint256 amount, address operator) internal {
        if (address(beforeUpdateHook) != address(0)) beforeUpdateHook.beforeUpdate(from, to, amount, operator);
    }

    /** @notice ERC20 transfer with hook call */
    function transfer(address to, uint256 amount) public override returns (bool) {
        _callBeforeUpdate(msg.sender, to, amount, msg.sender);
        return super.transfer(to, amount);
    }

    /** @notice ERC20 transferFrom with hook call */
    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        _callBeforeUpdate(from, to, amount, msg.sender);
        return super.transferFrom(from, to, amount);
    }

    /* ========================================= RECEIVE ========================================= */

    /** @notice Accept ETH for gas refunds or native token strategies */
    receive() external payable {}
}

project/contracts/core/DelayedWithdraw.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {WETH} from "solmate/src/tokens/WETH.sol";
import {BoringVault} from "./BoringVault.sol";
import {AccountantProviders} from "./AccountantProviders.sol";
import {Teller} from "./Teller.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";

import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {IDelayedWithdraw} from "../interfaces/IDelayedWithdraw.sol";

import "../auth/PrimeAuth.sol";

contract DelayedWithdraw is PrimeAuth, ReentrancyGuard, IDelayedWithdraw {
    using SafeTransferLib for BoringVault;
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;

    // ========================================= STRUCTS =========================================

    /**
     * @param withdrawDelay The delay in seconds before a requested withdrawal can be completed.
     * @param outstandingShares The total number of shares that are currently outstanding for an asset.
     * @param withdrawFee The fee that is charged when a withdrawal is completed.
     * @param expeditedWithdrawFee The fee charged to accelerate withdrawal to 1 day (in basis points).
     */
    struct WithdrawState {
        uint32 withdrawDelay;
        uint128 outstandingShares;
        uint16 withdrawFee;
        uint16 expeditedWithdrawFee;
    }

    /**
     * @param allowThirdPartyToComplete Whether or not a 3rd party can complete a withdraw on behalf of a user.
     * @param maturity The time at which the withdrawal can be completed.
     * @param shares The number of shares that are requested to be withdrawn.
     * @param sharesFee The total fee in shares that will be charged.
     */
    struct WithdrawRequest {
        bool allowThirdPartyToComplete;
        uint40 maturity;
        uint96 shares;
        uint96 sharesFee;
    }

    // ========================================= CONSTANTS =========================================

    /**
     * @notice The largest withdraw fee that can be set. (max 20%).
     */
    uint16 internal constant MAX_WITHDRAW_FEE = 0.2e4;

    /**
     * @notice The expedited withdraw delay - 1 day in seconds.
     */
    uint32 internal constant EXPEDITED_WITHDRAW_DELAY = 1 days;

    // ========================================= STATE =========================================

    /**
     * @notice The address that receives the fee when a withdrawal is completed.
     */
    address public feeAddress;

    /**
     * @notice The withdrawal settings for the asset.
     */
    WithdrawState public withdrawState;

    /**
     * @notice The mapping of users to their withdrawal requests.
     */
    mapping(address => WithdrawRequest) public withdrawRequests;

    //============================== IMMUTABLES ===============================

    /**
     * @notice The accountant contract that is used to get the exchange rate of assets.
     */
    AccountantProviders internal immutable accountant;

    /**
     * @notice The teller contract that users are depositing to.
     */
    Teller internal immutable teller;

    /**
     * @notice The BoringVault contract that users are withdrawing from.
     */
    BoringVault public immutable boringVault;

    /**
     * @notice The asset that can be withdrawn from the vault.
     */
    ERC20 public immutable asset;

    /**
     * @notice Constant that represents 1 share.
     */
    uint256 internal immutable ONE_SHARE;

    constructor(
        address _primeRBAC,
        address _vault,
        address _accountant,
        address _teller,
        address _payoutAddress
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        accountant = AccountantProviders(_accountant);
        teller = Teller(_teller);
        boringVault = BoringVault(payable(_vault));
        asset = ERC20(address(boringVault.asset()));
        ONE_SHARE = 10 ** boringVault.decimals();
        if (_payoutAddress == address(0)) revert DelayedWithdraw__BadAddress();
        feeAddress = _payoutAddress;
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Sets up the withdrawal settings.
     * @dev Callable by OWNER_ROLE.
     */
    function setupWithdraw(uint32 withdrawDelay, uint16 withdrawFee, uint16 expeditedWithdrawFee) external onlyProtocolAdmin {
        if (withdrawFee > MAX_WITHDRAW_FEE) revert DelayedWithdraw__WithdrawFeeTooHigh();
        if (expeditedWithdrawFee > MAX_WITHDRAW_FEE) revert DelayedWithdraw__ExpeditedWithdrawFeeTooHigh();

        if (withdrawState.withdrawDelay != 0) revert DelayedWithdraw__AlreadySetup();
        withdrawState.withdrawDelay = withdrawDelay;
        withdrawState.withdrawFee = withdrawFee;
        withdrawState.expeditedWithdrawFee = expeditedWithdrawFee;

        emit SetupWithdrawalsInAsset(address(asset), withdrawDelay, withdrawFee);
    }

    /**
     * @notice Changes the withdraw delay.
     * @dev Callable by PROTOCOL_ADMIN_ROLE.
     */
    function changeWithdrawDelay(uint32 withdrawDelay) external onlyProtocolAdmin {
        if (isPaused) revert DelayedWithdraw__Paused();

        withdrawState.withdrawDelay = withdrawDelay;

        emit WithdrawDelayUpdated(address(asset), withdrawDelay);
    }

    /**
     * @notice Changes the withdraw fee.
     * @dev Callable by PROTOCOL_ADMIN_ROLE.
     */
    function changeWithdrawFee(uint16 withdrawFee) external onlyProtocolAdmin {
        if (isPaused) revert DelayedWithdraw__Paused();

        if (withdrawFee > MAX_WITHDRAW_FEE) revert DelayedWithdraw__WithdrawFeeTooHigh();

        withdrawState.withdrawFee = withdrawFee;

        emit WithdrawFeeUpdated(address(asset), withdrawFee);
    }

    /**
     * @notice Changes the fee address.
     * @dev Callable by PROTOCOL_ADMIN_ROLE.
     */
    function setFeeAddress(address _feeAddress) external onlyProtocolAdmin {
        if (_feeAddress == address(0)) revert DelayedWithdraw__BadAddress();
        feeAddress = _feeAddress;

        emit FeeAddressSet(_feeAddress);
    }

    /**
     * @notice Sets the expedited withdraw fee.
     * @dev Callable by OWNER_ROLE.
     * @param _expeditedWithdrawFee Fee in basis points (e.g., 500 = 5%).
     */
    function changeExpeditedWithdrawFee(uint16 _expeditedWithdrawFee) external onlyProtocolAdmin {
        if (_expeditedWithdrawFee > MAX_WITHDRAW_FEE) {
            revert DelayedWithdraw__ExpeditedWithdrawFeeTooHigh();
        }
        withdrawState.expeditedWithdrawFee = _expeditedWithdrawFee;

        emit ExpeditedWithdrawFeeUpdated(_expeditedWithdrawFee);
    }

    /**
     * @notice Cancels a user's withdrawal request.
     * @dev Callable by OPERATOR_ROLE.
     */
    function cancelUserWithdraw(address user) external onlyOperator {
        _cancelWithdraw(user);
    }

    /**
     * @notice Completes a user's withdrawal request.
     * @dev Admins can complete requests even if they are outside the completion window.
     * @dev Callable by OPERATOR_ROLE.
     */
    function completeUserWithdraw(address user, uint256 minimumAssets) external onlyOperator returns (uint256 assetsOut) {
        WithdrawRequest storage req = withdrawRequests[user];
        assetsOut = _completeWithdraw(user, req, minimumAssets);
    }

    /**
     * @notice Withdraws a non boring token from the contract.
     * @dev Callable by PROTOCOL_ADMIN_ROLE only.
     * @dev Explicitly reverts if token is the BoringVault to prevent unauthorized share withdrawals.
     * @param token The ERC20 token to withdraw
     * @param amount The amount to withdraw (use type(uint256).max for full balance)
     */
    function withdrawNonBoringToken(ERC20 token, uint256 amount) external onlyProtocolAdmin {
        if (address(token) == address(boringVault)) revert DelayedWithdraw__CannotWithdrawBoringToken();
        if (amount == type(uint256).max) {
            amount = token.balanceOf(address(this));
        }
        token.safeTransfer(msg.sender, amount);
    }

    // ========================================= PUBLIC FUNCTIONS =========================================

    /**
     * @notice Allows a user to set whether or not a 3rd party can complete withdraws on behalf of them.
     */
    function setAllowThirdPartyToComplete(bool allow) external requiresAuth {
        withdrawRequests[msg.sender].allowThirdPartyToComplete = allow;

        emit ThirdPartyCompletionChanged(msg.sender, asset, allow);
    }

    /**
     * @notice Requests a withdrawal of shares.
     * @dev Publicly callable.
     */
    function requestWithdraw(uint96 shares, bool allowThirdPartyToComplete) external requiresAuth nonReentrant {
        if (isPaused) revert DelayedWithdraw__Paused();

        WithdrawRequest storage req = withdrawRequests[msg.sender];
        if (req.shares > 0) revert DelayedWithdraw__WithdrawPending();

        boringVault.safeTransferFrom(msg.sender, address(this), shares);

        withdrawState.outstandingShares += shares;

        req.shares = shares;
        uint40 maturity = uint40(block.timestamp + withdrawState.withdrawDelay);
        req.maturity = maturity;
        req.allowThirdPartyToComplete = allowThirdPartyToComplete;

        // Calculate and store fee at time of request
        uint256 fee = uint256(shares).mulDivDown(withdrawState.withdrawFee, 1e4);
        req.sharesFee = uint96(fee);

        emit WithdrawRequested(msg.sender, asset, shares, maturity);
    }

    /**
     * @notice Accelerates a pending withdrawal to 1 day by paying an additional fee.
     * @dev User must have an existing pending withdrawal. The acceleration fee is added to existing fees.
     * @dev Publicly callable.
     */
    function accelerateWithdraw() external requiresAuth nonReentrant {
        if (isPaused) revert DelayedWithdraw__Paused();
        if (withdrawState.expeditedWithdrawFee == 0) revert DelayedWithdraw__ExpeditedWithdrawNotAvailable();
        if (withdrawState.withdrawDelay <= EXPEDITED_WITHDRAW_DELAY) {
            revert DelayedWithdraw__ExpeditedWithdrawNotAvailable();
        }

        WithdrawRequest storage req = withdrawRequests[msg.sender];
        if (req.shares == 0) revert DelayedWithdraw__NoWithdrawToAccelerate();

        // Calculate acceleration fee and add to existing sharesFee
        uint256 accelerationFee = uint256(req.shares).mulDivDown(withdrawState.expeditedWithdrawFee, 1e4);
        req.sharesFee += uint96(accelerationFee);

        // Set new maturity to 1 day from now
        uint40 newMaturity = uint40(block.timestamp + EXPEDITED_WITHDRAW_DELAY);
        req.maturity = newMaturity;

        emit WithdrawAccelerated(msg.sender, asset, newMaturity, uint96(accelerationFee));
    }

    /**
     * @notice Cancels msg.sender's withdrawal request.
     * @dev Publicly callable.
     */
    function cancelWithdraw() external requiresAuth nonReentrant {
        _cancelWithdraw(msg.sender);
    }

    /**
     * @notice Completes a user's withdrawal request.
     * @dev Publicly callable. No time limit after maturity.
     * @param account Address of the user whose withdrawal to complete
     * @param minimumAssets Minimum amount of assets to receive (slippage protection)
     */
    function completeWithdraw(address account, uint256 minimumAssets) external requiresAuth nonReentrant returns (uint256 assetsOut) {
        if (isPaused) revert DelayedWithdraw__Paused();
        WithdrawRequest storage req = withdrawRequests[account];

        if (msg.sender != account && !req.allowThirdPartyToComplete) {
            revert DelayedWithdraw__ThirdPartyCompletionNotAllowed();
        }
        assetsOut = _completeWithdraw(account, req, minimumAssets);
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Helper function to view the outstanding withdraw debt.
     */
    function viewOutstandingDebt() public view returns (uint256 debt) {
        uint256 rate = accountant.getRateSafe();

        debt = rate.mulDivDown(withdrawState.outstandingShares, ONE_SHARE);
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Internal helper function that implements shared logic for cancelling a user's withdrawal request.
     */
    function _cancelWithdraw(address account) internal {
        // We do not check if `asset` is allowed, to handle edge cases where the asset is no longer allowed.

        WithdrawRequest storage req = withdrawRequests[account];
        uint96 shares = req.shares;
        if (shares == 0) revert DelayedWithdraw__NoSharesToWithdraw();
        withdrawState.outstandingShares -= shares;
        req.shares = 0;
        boringVault.safeTransfer(account, shares);

        emit WithdrawCancelled(account, asset, shares);
    }

    /**
     * @notice Internal helper function that implements shared logic for completing a user's withdrawal request.
     * @param account Address of the user
     * @param req Storage pointer to the user's withdraw request
     * @param minimumAssets Minimum amount of assets to receive (slippage protection)
     */
    function _completeWithdraw(address account, WithdrawRequest storage req, uint256 minimumAssets) internal returns (uint256 assetsOut) {
        if (isPaused) revert DelayedWithdraw__Paused();

        if (block.timestamp < req.maturity) revert DelayedWithdraw__WithdrawNotMatured();
        if (req.shares == 0) revert DelayedWithdraw__NoSharesToWithdraw();

        uint256 shares = req.shares;
        uint256 sharesFee = req.sharesFee;

        // Safe to cast shares to a uint128 since req.shares is constrained to be less than 2^96.
        withdrawState.outstandingShares -= uint128(shares);

        // Deduct fee from shares and transfer to fee address
        if (sharesFee > 0) {
            shares -= sharesFee;
            boringVault.safeTransfer(feeAddress, sharesFee);
        }

        req.shares = 0;
        req.sharesFee = 0;

        assetsOut = teller.withdraw(shares, minimumAssets, account);

        emit WithdrawCompleted(account, asset, shares, assetsOut);
    }

    //=========================================  VIEW FUNCTIONS =========================================

    function getWithdrawState() external view returns (WithdrawState memory) {
        return withdrawState;
    }

    function getWithdrawRequest(address account) external view returns (WithdrawRequest memory) {
        return withdrawRequests[account];
    }
}

project/contracts/core/Distributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {PrimeAuth} from "../auth/PrimeAuth.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";
import {BoringVault} from "./BoringVault.sol";
import {Teller} from "./Teller.sol";

/**
 * @title Distributor
 * @author Prime Vaults
 * @notice A multi-reward distributor contract integrated with PrimeVault
 * @dev Automatically tracks vault share balances without requiring explicit staking
 * Users earn multiple reward tokens based on their vault share balance
 * Integrates as a IBeforeUpdateHook to update rewards on every vault transfer
 */
contract Distributor is PrimeAuth, ReentrancyGuard, IBeforeUpdateHook {
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;

    // ========================================= STRUCTS =========================================

    struct RewardData {
        address rewardsToken;
        uint256 rewardsDuration;
        uint256 periodFinish;
        uint256 rewardRate;
        uint256 lastUpdateTime;
        uint256 rewardPerTokenStored;
    }

    // ========================================= STATE VARIABLES =========================================

    /// @notice The vault contract (reads balances directly)
    ERC20 public immutable vault;

    Teller public immutable teller;

    /// @notice Mapping from reward token address to its reward data
    mapping(address => RewardData) public rewardData;

    /// @notice Array of all reward token addresses
    address[] public rewardTokens;

    /// @notice user -> reward token -> amount of reward per token paid
    mapping(address => mapping(address => uint256)) public userRewardPerTokenPaid;

    /// @notice user -> reward token -> accumulated rewards
    mapping(address => mapping(address => uint256)) public rewards;

    /// @notice Compound fee percentage (in basis points, 10000 = 100%)
    uint256 public compoundFee;

    /// @notice Maximum compound fee (20%)
    uint256 public constant MAX_COMPOUND_FEE = 2000; // 20%

    /// @notice Mapping of user address to whether they allow third-party compounding
    mapping(address => bool) public allowThirdPartyCompound;

    /// @notice Treasury address - holds reward tokens to pay users
    address public treasury;

    // ========================================= EVENTS =========================================

    event RewardAdded(uint256 reward);
    event RewardPaid(address indexed user, address indexed rewardsToken, uint256 reward, address to);
    event RewardsDurationUpdated(address token, uint256 newDuration);
    event Recovered(address token, uint256 amount);
    event CompoundReward(address indexed account, address indexed rewardToken, uint256 rewardAmount, uint256 shares, uint256 fee);
    event CompoundFeeUpdated(uint256 newFee);
    event ThirdPartyCompoundAllowed(address indexed user, bool allowed);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);

    // ========================================= ERRORS =========================================

    error Distributor__AlreadyAdded();
    error Distributor__NotAuthorized();
    error Distributor__RewardPeriodActive();
    error Distributor__ZeroDuration();
    error Distributor__CannotWithdrawRewardToken();
    error Distributor__Paused();
    error Distributor__CompoundFeeExceedsMaximum();
    error Distributor__ThirdPartyCompoundNotAllowed();

    // ========================================= CONSTRUCTOR =========================================

    /**
     * @notice Initializes the Distributor contract
     * @param _primeRBAC The PrimeRBAC contract for authentication
     * @param _teller The vault contract (source of share balances)
     */
    constructor(address _primeRBAC, address _teller) PrimeAuth(_primeRBAC, address(BoringVault(payable(Teller(_teller).vault())).authority())) {
        teller = Teller(_teller);
        vault = ERC20(teller.vault());
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Add a new reward token to the contract
     * @param _rewardsToken The address of the reward token
     * @param _rewardsDuration The duration over which rewards will be distributed
     * @dev Callable by PROTOCOL_ADMIN_ROLE
     */
    function addReward(address _rewardsToken, uint256 _rewardsDuration) external onlyProtocolAdmin {
        if (rewardData[_rewardsToken].rewardsDuration != 0) revert Distributor__AlreadyAdded();

        rewardTokens.push(_rewardsToken);
        rewardData[_rewardsToken] = RewardData({
            rewardsToken: _rewardsToken,
            rewardsDuration: _rewardsDuration,
            periodFinish: 0,
            rewardRate: 0,
            lastUpdateTime: 0,
            rewardPerTokenStored: 0
        });
    }

    /**
     * @notice Update the rewards duration for a reward token
     * @param _rewardsToken The reward token address
     * @param _rewardsDuration The new rewards duration
     * @dev Can only be called after the current reward period has finished
     * @dev Callable by the rewards distributor
     */
    function setRewardsDuration(address _rewardsToken, uint256 _rewardsDuration) external onlyOperator {
        if (block.timestamp <= rewardData[_rewardsToken].periodFinish) {
            revert Distributor__RewardPeriodActive();
        }
        if (_rewardsDuration == 0) revert Distributor__ZeroDuration();

        rewardData[_rewardsToken].rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(_rewardsToken, rewardData[_rewardsToken].rewardsDuration);
    }

    /**
     * @notice Set the compound fee percentage
     * @param _compoundFee The new compound fee in basis points (10000 = 100%)
     * @dev Callable by PROTOCOL_ADMIN_ROLE
     * @dev Maximum fee is 20% (2000 basis points)
     */
    function setCompoundFee(uint256 _compoundFee) external onlyProtocolAdmin {
        if (_compoundFee > MAX_COMPOUND_FEE) revert Distributor__CompoundFeeExceedsMaximum();
        compoundFee = _compoundFee;
        emit CompoundFeeUpdated(_compoundFee);
    }

    /**
     * @notice Set the treasury address
     * @param _treasury The new treasury address (address(0) to disable)
     * @dev Callable by PROTOCOL_ADMIN_ROLE
     * @dev If treasury is set, rewards are transferred from treasury instead of contract balance
     */
    function setTreasury(address _treasury) external onlyProtocolAdmin {
        address oldTreasury = treasury;
        treasury = _treasury;
        emit TreasuryUpdated(oldTreasury, _treasury);
    }

    /**
     * @notice Recover ERC20 tokens sent to the contract by mistake
     * @param tokenAddress The token to recover
     * @param tokenAmount The amount to recover
     * @dev Cannot recover active reward tokens
     * @dev Callable by PROTOCOL_ADMIN_ROLE
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyProtocolAdmin {
        if (rewardData[tokenAddress].lastUpdateTime != 0) revert Distributor__CannotWithdrawRewardToken();

        ERC20(tokenAddress).safeTransfer(msg.sender, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    // ========================================= REWARD DISTRIBUTION FUNCTIONS =========================================

    /**
     * @notice Notify the contract of a new reward amount
     * @param _rewardsToken The reward token address
     * @param reward The amount of rewards to distribute
     * @dev Does NOT transfer tokens - admin promises to deposit later
     * @dev Use getRewardDebt() to check how much needs to be deposited
     * @dev Callable by OPERATOR_ROLE
     */
    function notifyRewardAmount(address _rewardsToken, uint256 reward) external onlyOperator updateReward(address(0)) {
        if (block.timestamp >= rewardData[_rewardsToken].periodFinish) {
            rewardData[_rewardsToken].rewardRate = reward / rewardData[_rewardsToken].rewardsDuration;
        } else {
            uint256 remaining = rewardData[_rewardsToken].periodFinish - block.timestamp;
            uint256 leftover = remaining * rewardData[_rewardsToken].rewardRate;
            rewardData[_rewardsToken].rewardRate = (reward + leftover) / rewardData[_rewardsToken].rewardsDuration;
        }

        rewardData[_rewardsToken].lastUpdateTime = block.timestamp;
        rewardData[_rewardsToken].periodFinish = block.timestamp + rewardData[_rewardsToken].rewardsDuration;

        emit RewardAdded(reward);
    }

    // ========================================= HOOK FUNCTIONS =========================================

    /**
     * @notice BeforeUpdate hook - updates rewards BEFORE balance changes
     * @param from The sender address (address(0) for mint)
     * @param to The recipient address (address(0) for burn)
     * @dev Called by Teller BEFORE any balance change (mint/burn/transfer)
     * @dev Must update rewards based on OLD balances before they change
     * @dev Callable by Teller only
     */
    function beforeUpdate(address from, address to, uint256 /* amount */, address /* operator */) public requiresAuth {
        if (isPaused) revert Distributor__Paused();

        // Update rewards for both parties BEFORE their balances change
        // address(0) means mint, skip sender update
        if (from != address(0)) {
            updateRewardForAccount(from);
        }
        // address(0) means burn, skip recipient update
        if (to != address(0)) {
            updateRewardForAccount(to);
        }
    }

    // ========================================= USER FUNCTIONS =========================================

    /**
     * @notice Claim accumulated rewards for a single token
     * @dev No staking required - rewards are based on vault share balance
     * @param _account The account to claim rewards for
     * @param _rewardToken The reward token address to claim
     * @param _to The address to send claimed rewards to
     * @return rewardAmount The amount of reward token claimed
     */
    function _claimReward(address _account, address _rewardToken, address _to) internal updateReward(_account) returns (uint256 rewardAmount) {
        rewardAmount = rewards[_account][_rewardToken];
        if (rewardAmount > 0) {
            rewards[_account][_rewardToken] = 0;

            // If treasury is set, transfer from treasury. Otherwise use contract balance
            if (treasury != address(0)) {
                ERC20(_rewardToken).safeTransferFrom(treasury, _to, rewardAmount);
            } else {
                ERC20(_rewardToken).safeTransfer(_to, rewardAmount);
            }

            emit RewardPaid(_account, _rewardToken, rewardAmount, _to);
        }
    }

    /**
     * @notice Claim accumulated rewards for multiple tokens
     * @dev No staking required - rewards are based on vault share balance
     * @param _rewardTokens Array of reward token addresses to claim
     * @return rewardsOut Array of amounts claimed for each reward token
     */
    function claimRewards(address[] memory _rewardTokens) public requiresAuth returns (uint256[] memory rewardsOut) {
        if (isPaused) revert Distributor__Paused();

        rewardsOut = new uint256[](_rewardTokens.length);
        for (uint256 i = 0; i < _rewardTokens.length; i++) {
            rewardsOut[i] = _claimReward(msg.sender, _rewardTokens[i], msg.sender);
        }
    }

    /**
     * @notice Allow or disallow third-party compounding
     * @param _allowed Whether to allow third parties to compound rewards for the user
     * @dev Publicly callable by anyone for their own account
     */
    function setAllowThirdPartyCompound(bool _allowed) external requiresAuth {
        allowThirdPartyCompound[msg.sender] = _allowed;
        emit ThirdPartyCompoundAllowed(msg.sender, _allowed);
    }

    /**
     * @notice Compounds a specific reward token by claiming and re-depositing it.
     * @dev Can be called by the user themselves (no fee) or by third parties (with fee if enabled).
     * @dev Only works with tokens that can be deposited into the vault (typically the base asset).
     * @param _account The account to compound rewards for
     */
    function compoundReward(address _account) external updateReward(_account) requiresAuth {
        if (isPaused) revert Distributor__Paused();

        ERC20 asset = BoringVault(payable(address(vault))).asset();
        // Check authorization: user can compound for themselves, or third party if allowed
        if (msg.sender != _account) {
            if (!allowThirdPartyCompound[_account]) {
                revert Distributor__ThirdPartyCompoundNotAllowed();
            }
        }

        uint256 rewardAmount = rewards[_account][address(asset)];
        if (rewardAmount > 0) {
            rewards[_account][address(asset)] = 0;

            uint256 fee = 0;
            uint256 amountToCompound = rewardAmount;

            // If treasury is set, transfer from treasury to this contract first
            if (treasury != address(0)) {
                asset.safeTransferFrom(treasury, address(this), rewardAmount);
            }

            // Charge fee if third party is calling and fee is set
            if (msg.sender != _account && compoundFee > 0) {
                fee = (rewardAmount * compoundFee) / 1e4;
                amountToCompound = rewardAmount - fee;

                // Transfer fee to caller
                if (fee > 0) {
                    asset.safeTransfer(msg.sender, fee);
                }
            }

            // Compound remaining amount
            if (amountToCompound > 0) {
                asset.safeApprove(address(vault), amountToCompound);
                uint256 shares = teller.deposit(amountToCompound, 0, _account);
                emit CompoundReward(_account, address(asset), amountToCompound, shares, fee);
            }
        }
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Get the total supply of vault shares
     * @dev Reads directly from vault
     */
    function totalSupply() public view returns (uint256) {
        return vault.totalSupply();
    }

    /**
     * @notice Get the vault share balance of an account
     * @param account The account to query
     * @dev Reads directly from vault
     */
    function balanceOf(address account) public view returns (uint256) {
        return vault.balanceOf(account);
    }

    /**
     * @notice Get the last time rewards are applicable
     * @param _rewardsToken The reward token address
     */
    function lastTimeRewardApplicable(address _rewardsToken) public view returns (uint256) {
        return block.timestamp < rewardData[_rewardsToken].periodFinish ? block.timestamp : rewardData[_rewardsToken].periodFinish;
    }

    /**
     * @notice Calculate the current reward per token
     * @param _rewardsToken The reward token address
     */
    function rewardPerToken(address _rewardsToken) public view returns (uint256) {
        if (vault.totalSupply() == 0) {
            return rewardData[_rewardsToken].rewardPerTokenStored;
        }

        return
            rewardData[_rewardsToken].rewardPerTokenStored +
            ((lastTimeRewardApplicable(_rewardsToken) - rewardData[_rewardsToken].lastUpdateTime) * rewardData[_rewardsToken].rewardRate * 1e18) /
                vault.totalSupply();
    }

    /**
     * @notice Calculate how much reward an account has earned
     * @param account The account to query
     * @param _rewardsToken The reward token address
     */
    function earned(address account, address _rewardsToken) public view returns (uint256) {
        return
            (vault.balanceOf(account) * (rewardPerToken(_rewardsToken) - userRewardPerTokenPaid[account][_rewardsToken])) / 1e18 +
            rewards[account][_rewardsToken];
    }

    /**
     * @notice Get the total reward for the full duration
     * @param _rewardsToken The reward token address
     */
    function getRewardForDuration(address _rewardsToken) external view returns (uint256) {
        return rewardData[_rewardsToken].rewardRate * rewardData[_rewardsToken].rewardsDuration;
    }

    /**
     * @notice Calculate total reward debt (how much needs to be deposited)
     * @param _rewardsToken The reward token address
     * @return debt The amount of reward tokens that should be in the contract to cover all promises
     * @dev This is the total amount users can claim based on current rewards accounting
     * @dev Admin should deposit (debt - currentBalance) to fulfill promises
     */
    function getRewardDebt(address _rewardsToken) external view returns (uint256 debt) {
        uint256 currentTime = block.timestamp;
        uint256 applicableTime = currentTime < rewardData[_rewardsToken].periodFinish ? currentTime : rewardData[_rewardsToken].periodFinish;

        // Calculate total rewards distributed so far
        uint256 timeElapsed = applicableTime > rewardData[_rewardsToken].lastUpdateTime ? applicableTime - rewardData[_rewardsToken].lastUpdateTime : 0;

        uint256 rewardsSinceLastUpdate = timeElapsed * rewardData[_rewardsToken].rewardRate;

        // Total debt includes:
        // 1. Already accumulated reward per token * total supply
        // 2. New rewards since last update
        uint256 supply = vault.totalSupply();
        if (supply > 0) {
            uint256 currentRewardPerToken = rewardData[_rewardsToken].rewardPerTokenStored + (rewardsSinceLastUpdate * 1e18) / supply;
            debt = (currentRewardPerToken * supply) / 1e18;
        }
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Updates reward accounting for a specific account
     * @param account The account to update
     * @dev Can be called publicly to manually update rewards before balance queries
     */
    function updateRewardForAccount(address account) public {
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            address token = rewardTokens[i];
            rewardData[token].rewardPerTokenStored = rewardPerToken(token);
            rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);

            rewards[account][token] = earned(account, token);
            userRewardPerTokenPaid[account][token] = rewardData[token].rewardPerTokenStored;
        }
    }

    // ========================================= MODIFIERS =========================================

    /**
     * @notice Updates reward accounting for an account
     * @param account The account to update (address(0) updates global state only)
     */
    modifier updateReward(address account) {
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            address token = rewardTokens[i];
            rewardData[token].rewardPerTokenStored = rewardPerToken(token);
            rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);

            if (account != address(0)) {
                rewards[account][token] = earned(account, token);
                userRewardPerTokenPaid[account][token] = rewardData[token].rewardPerTokenStored;
            }
        }
        _;
    }

    // =========================== VIEW HELPERS FOR FRONTEND ===========================

    /**
     * @notice Get all rewards tokens with full RewardData
     */
    function getRewards() external view returns (RewardData[] memory) {
        RewardData[] memory data = new RewardData[](rewardTokens.length);
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            data[i] = rewardData[rewardTokens[i]];
        }
        return data;
    }
}

project/contracts/core/ManagerWithMerkle.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Auth, Authority} from "solmate/src/auth/Auth.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {MerkleProofLib} from "solmate/src/utils/MerkleProofLib.sol";
import {BoringVault} from "./BoringVault.sol";
import {IManager} from "../interfaces/IManager.sol";

import "../auth/PrimeAuth.sol";

contract ManagerWithMerkleVerification is PrimeAuth, IManager {
    using FixedPointMathLib for uint256;
    using Address for address;

    // ========================================= STATE =========================================

    /**
     * @notice The BoringVault this contract manages
     */
    BoringVault public immutable vault;

    /**
     * @notice A merkle tree root that restricts what data can be passed to the BoringVault.
     * @dev Maps a strategist address to their specific merkle root.
     * @dev Each leaf is composed of the keccak256 hash of abi.encodePacked {decodersAndSanitizer, target, valueIsNonZero, selector, argumentAddress_0, ...., argumentAddress_N}
     *      Where:
     *             - decodersAndSanitizer is the addres to call to extract packed address arguments from the calldata
     *             - target is the address to make the call to
     *             - valueIsNonZero is a bool indicating whether or not the value is non-zero
     *             - selector is the function selector on target
     *             - argumentAddress is each allowed address argument in that call
     */
    mapping(address => bytes32) public manageRoot;

    // ========================================= CONSTRUCTOR =========================================

    constructor(address _primeRBAC, address _vault) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = BoringVault(payable(_vault));
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Sets the Merkle root for a strategist
     * @param strategist Address of the strategist
     * @param _manageRoot The new Merkle root
     * @dev Callable by ADMIN_ROLE
     */
    function setManageRoot(address strategist, bytes32 _manageRoot) external onlyProtocolAdmin {
        bytes32 oldRoot = manageRoot[strategist];
        manageRoot[strategist] = _manageRoot;
        emit ManageRootUpdated(strategist, oldRoot, _manageRoot);
    }

    // ========================================= STRATEGIST FUNCTIONS =========================================

    /**
     * @notice Allows strategist to manage the BoringVault.
     * @dev The strategist must provide a merkle proof for every call that verifiees they are allowed to make that call.
     * @dev Callable by MANAGER_INTERNAL_ROLE.
     * @dev Callable by STRATEGIST_ROLE.
     * @dev Callable by MICRO_MANAGER_ROLE.
     */
    function manageVaultWithMerkleVerification(
        bytes32[][] calldata manageProofs,
        address[] calldata decodersAndSanitizers,
        address[] calldata targets,
        bytes[] calldata targetData,
        uint256[] calldata values
    ) external requiresAuth {
        if (isPaused) revert ManagerWithMerkleVerification__Paused();
        uint256 targetsLength = targets.length;
        if (targetsLength != manageProofs.length) revert ManagerWithMerkleVerification__InvalidManageProofLength();
        if (targetsLength != targetData.length) revert ManagerWithMerkleVerification__InvalidTargetDataLength();
        if (targetsLength != values.length) revert ManagerWithMerkleVerification__InvalidValuesLength();
        if (targetsLength != decodersAndSanitizers.length) {
            revert ManagerWithMerkleVerification__InvalidDecodersAndSanitizersLength();
        }

        bytes32 strategistManageRoot = manageRoot[msg.sender];
        uint256 totalSupply = vault.totalSupply();

        for (uint256 i; i < targetsLength; ++i) {
            _verifyCallData(strategistManageRoot, manageProofs[i], decodersAndSanitizers[i], targets[i], values[i], targetData[i]);
            vault.manage(targets[i], targetData[i], values[i]);
        }
        if (totalSupply != vault.totalSupply()) {
            revert ManagerWithMerkleVerification__TotalSupplyMustRemainConstantDuringPlatform();
        }
        emit BoringVaultManaged(targetsLength);
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Helper function to decode, sanitize, and verify call data.
     */
    function _verifyCallData(
        bytes32 currentManageRoot,
        bytes32[] calldata manageProof,
        address decoderAndSanitizer,
        address target,
        uint256 value,
        bytes calldata targetData
    ) internal view {
        // Use address decoder to get addresses in call data.
        bytes memory packedArgumentAddresses = abi.decode(decoderAndSanitizer.functionStaticCall(targetData), (bytes));
        if (!_verifyManageProof(currentManageRoot, manageProof, target, decoderAndSanitizer, value, bytes4(targetData), packedArgumentAddresses)) {
            revert ManagerWithMerkleVerification__FailedToVerifyManageProof(target, targetData, value);
        }
    }

    /**
     * @notice Helper function to verify a manageProof is valid.
     */
    function _verifyManageProof(
        bytes32 root,
        bytes32[] calldata proof,
        address target,
        address decoderAndSanitizer,
        uint256 value,
        bytes4 selector,
        bytes memory packedArgumentAddresses
    ) internal pure returns (bool) {
        bool valueNonZero = value > 0;
        bytes32 leaf = keccak256(abi.encodePacked(decoderAndSanitizer, target, valueNonZero, selector, packedArgumentAddresses));
        return MerkleProofLib.verify(proof, root, leaf);
    }
}

project/contracts/core/Teller.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {BoringVault} from "./BoringVault.sol";
import {AccountantProviders} from "./AccountantProviders.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";
import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {Distributor} from "./Distributor.sol";
import {ITeller} from "../interfaces/ITeller.sol";

import "../auth/PrimeAuth.sol";

contract Teller is PrimeAuth, IBeforeUpdateHook, ReentrancyGuard, ITeller {
    using FixedPointMathLib for uint256;
    using SafeTransferLib for ERC20;

    // ========================================= STRUCTS =========================================

    /**
     * @param allowDeposits whether deposits are allowed for the asset
     * @param allowWithdraws whether withdrawals are allowed for the asset
     * @param shareLockPeriod after deposits, shares are locked to the msg.sender's address for this period
     * @param depositCap the global deposit cap of the vault
     */
    struct TellerState {
        bool allowDeposits;
        bool allowWithdraws;
        uint64 shareLockPeriod;
        uint112 depositCap;
    }

    /**
     * @param shareUnlockTime uint256 indicating the time at which the shares will be unlocked.
     */
    struct BeforeTransferData {
        uint256 shareUnlockTime;
    }

    // ========================================= CONSTANTS =========================================

    /**
     * @notice The maximum possible share lock period.
     */
    uint256 internal constant MAX_SHARE_LOCK_PERIOD = 7 days;

    // ========================================= STATE =========================================

    /**
     * @notice Store the teller state in a packed slot.
     */
    TellerState public tellerState;

    /**
     * @notice Maps address to BeforeTransferData struct to check if shares are locked and if the address is on any allow or deny list.
     */
    mapping(address => BeforeTransferData) public beforeTransferData;

    //============================== IMMUTABLES ===============================
    /**
     * @notice The single asset this teller supports
     */
    ERC20 public immutable asset;

    /**
     * @notice The BoringVault this contract is working with.
     */
    BoringVault public immutable vault;

    /**
     * @notice The AccountantProviders this contract is working with.
     */
    AccountantProviders public immutable accountant;

    /**
     * @notice The Distributor contract for reward distribution (optional).
     */
    Distributor public distributor;

    /**
     * @notice One share of the BoringVault.
     */
    uint256 internal immutable ONE_SHARE;

    constructor(address _primeRBAC, address _vault, address _accountant) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = BoringVault(payable(_vault));
        ONE_SHARE = 10 ** vault.decimals();
        accountant = AccountantProviders(_accountant);
        asset = vault.asset();
        tellerState = TellerState({allowDeposits: true, allowWithdraws: true, shareLockPeriod: 0, depositCap: type(uint112).max});
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Enable or disable deposits for the asset.
     * @dev Callable by OWNER_ROLE.
     */
    function setAllowDeposits(bool _allowDeposits) external onlyProtocolAdmin {
        tellerState.allowDeposits = _allowDeposits;
        emit DepositsAllowed(_allowDeposits);
    }

    /**
     * @notice Enable or disable withdrawals for the asset.
     * @dev Callable by OWNER_ROLE.
     */
    function setAllowWithdraws(bool _allowWithdraws) external onlyProtocolAdmin {
        tellerState.allowWithdraws = _allowWithdraws;
        emit WithdrawsAllowed(_allowWithdraws);
    }

    /**
     * @notice Set the Distributor contract for reward distribution.
     * @dev Callable by OWNER_ROLE.
     */
    function setDistributor(address _distributor) external onlyProtocolAdmin {
        distributor = Distributor(_distributor);
    }

    /**
     * @notice Sets the share lock period.
     * @dev This locks shares to the user address for the specified period.
     * @dev Callable by PROTOCOL_ADMIN_ROLE.
     */
    function setShareLockPeriod(uint64 _shareLockPeriod) external onlyProtocolAdmin {
        if (_shareLockPeriod > MAX_SHARE_LOCK_PERIOD) revert Teller__ShareLockPeriodTooLong();
        tellerState.shareLockPeriod = _shareLockPeriod;
    }

    /**
     * @notice Set the deposit cap of the vault.
     * @dev Callable by PROTOCOL_ADMIN_ROLE
     */
    function setDepositCap(uint112 cap) external onlyProtocolAdmin {
        tellerState.depositCap = cap;
        emit DepositCapSet(cap);
    }

    // ========================================= IBeforeUpdateHook FUNCTIONS =========================================

    /**
     * @notice Implement beforeUpdate hook to check if shares are locked.
     * @notice If share lock period is set to zero, then users will be able to mint and transfer in the same tx.
     *         if this behavior is not desired then a share lock period of >=1 should be used.
     * @notice When minting (from = address(0)), only checks `to`.
     * @notice When burning (to = address(0)), only checks `from`.
     */
    function beforeUpdate(address from, address to, uint256 /* amount */, address /* operator */) public virtual {
        _getAccountant().updateExchangeRate();
        // Update rewards BEFORE balance changes if distributor is set
        if (address(distributor) != address(0)) {
            if (from != address(0)) {
                distributor.updateRewardForAccount(from);
            }
            if (to != address(0)) {
                distributor.updateRewardForAccount(to);
            }
        }

        // For transfers (not mint/burn): check if shares are locked
        if (from != address(0) && beforeTransferData[from].shareUnlockTime > block.timestamp) {
            revert Teller__SharesAreLocked();
        }
    }

    // ========================================= USER FUNCTIONS =========================================

    /**
     * @notice Allows users to deposit into the BoringVault, if this contract is not paused.
     * @dev Publicly callable.
     */
    function deposit(uint256 depositAmount, uint256 minimumMint, address to) external virtual requiresAuth nonReentrant returns (uint256 shares) {
        if (to == address(0)) to = msg.sender;
        _beforeDeposit();
        shares = _erc20Deposit(depositAmount, minimumMint, msg.sender, to);
        _afterPublicDeposit(to, depositAmount, shares, tellerState.shareLockPeriod);
    }

    /**
     * @notice Allows on ramp role to deposit into this contract.
     * @dev Does NOT support native deposits.
     * @dev Callable by SOLVER_ROLE.
     */
    function bulkDeposit(uint256 depositAmount, uint256 minimumMint, address to) external virtual requiresAuth nonReentrant returns (uint256 shares) {
        _beforeDeposit();
        shares = _erc20Deposit(depositAmount, minimumMint, msg.sender, to);
        emit BulkDeposit(address(asset), depositAmount);
    }

    /**
     * @notice Allows withdrawals from this contract.
     * @dev Either public or disabled depending on configuration.
     */
    function withdraw(uint256 shareAmount, uint256 minimumAssets, address to) external virtual requiresAuth nonReentrant returns (uint256 assetsOut) {
        _getAccountant().updateExchangeRate();
        assetsOut = _withdraw(shareAmount, minimumAssets, to);
        emit Withdraw(address(asset), shareAmount);
    }

    // ========================================= INTERNAL HELPER FUNCTIONS =========================================

    /**
     * @notice Implements a common ERC20 deposit into BoringVault.
     */
    function _erc20Deposit(uint256 depositAmount, uint256 minimumMint, address from, address to) internal virtual returns (uint256 shares) {
        _getAccountant().updateExchangeRate();
        TellerState storage state = tellerState;
        if (depositAmount == 0) revert Teller__ZeroAssets();
        shares = depositAmount.mulDivDown(ONE_SHARE, accountant.getRate());
        if (shares < minimumMint) revert Teller__MinimumMintNotMet();
        if (state.depositCap != type(uint112).max) {
            uint256 totalSharesAfterDeposit = shares + vault.totalSupply();
            if (totalSharesAfterDeposit > state.depositCap) revert Teller__DepositExceedsCap(totalSharesAfterDeposit, state.depositCap);
        }
        vault.enter(from, depositAmount, to, shares);
        _afterDeposit(depositAmount);
    }

    /**
     * @notice Implements a common ERC20 withdraw from BoringVault.
     */
    function _withdraw(uint256 shareAmount, uint256 minimumAssets, address to) internal virtual returns (uint256 assetsOut) {
        TellerState storage state = tellerState;
        if (isPaused) revert Teller__Paused();
        if (!state.allowWithdraws) revert Teller__WithdrawsNotAllowed();

        if (shareAmount == 0) revert Teller__ZeroShares();
        assetsOut = shareAmount.mulDivDown(accountant.getRate(), ONE_SHARE);
        if (assetsOut < minimumAssets) revert Teller__MinimumAssetsNotMet();
        _beforeWithdraw(assetsOut);
        vault.exit(to, assetsOut, msg.sender, shareAmount);
    }

    /**
     * @notice Helper function to cast from base accountant type to yield streaming accountant
     */
    function _getAccountant() internal view returns (AccountantProviders) {
        return AccountantProviders(address(accountant));
    }

    /**
     * @notice Handle pre-deposit checks.
     */
    function _beforeDeposit() internal view {
        TellerState storage state = tellerState;
        if (isPaused) revert Teller__Paused();
        if (!state.allowDeposits) revert Teller__DepositsNotAllowed();
    }

    /**
     * @notice Handle share lock logic, and event.
     */
    function _afterPublicDeposit(address user, uint256 depositAmount, uint256 shares, uint256 currentShareLockPeriod) internal {
        // Only set share unlock time if share lock period is greater than 0.
        if (currentShareLockPeriod > 0) {
            beforeTransferData[user].shareUnlockTime = block.timestamp + currentShareLockPeriod;
        }
        emit Deposit(
            0, // nonce no longer used
            user,
            depositAmount,
            shares,
            block.timestamp,
            currentShareLockPeriod
        );
    }

    /**
     * @notice Handle permit logic.
     */
    function _handlePermit(uint256 depositAmount, uint256 deadline, uint8 v, bytes32 r, bytes32 s) internal {
        try asset.permit(msg.sender, address(vault), depositAmount, deadline, v, r, s) {} catch {
            if (asset.allowance(msg.sender, address(vault)) < depositAmount) {
                revert Teller__PermitFailedAndAllowanceTooLow();
            }
        }
    }

    /**
     * @notice Hook that is called after a deposit operation.
     * @dev Can be overridden by child contracts to implement custom post-deposit logic.
     * @param assetAmount The amount of the asset that was deposited.
     */
    function _afterDeposit(uint256 assetAmount) internal virtual {}

    /**
     * @notice Hook that is called before a withdrawal operation.
     * @dev Can be overridden by child contracts to implement custom pre-withdrawal logic.
     * @param assetAmount The amount of the asset that will be withdrawn.
     */
    function _beforeWithdraw(uint256 assetAmount) internal virtual {}

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Returns the version of the contract.
     */
    function version() public pure virtual returns (string memory) {
        return "Base V0.1";
    }

    /**
     * @notice Get the current teller state.
     */
    function getTellerState() external view returns (TellerState memory) {
        return tellerState;
    }
}

