// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {IRateProvider} from "../interfaces/IRateProvider.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {BoringVault} from "./BoringVault.sol";
import {IPausable} from "../interfaces/IPausable.sol";

import {PrimeAuth} from "../auth/PrimeAuth.sol";

contract AccountantWithRateProviders is PrimeAuth, IRateProvider, IPausable {
    using FixedPointMathLib for uint256;
    using SafeTransferLib for ERC20;

    // ========================================= STRUCTS =========================================

    /**
     * @param payoutAddress the address `claimFees` sends fees to
     * @param feesOwedInBase total pending fees owed in terms of base
     * @param totalSharesLastUpdate total amount of shares the last exchange rate update
     * @param exchangeRate the current exchange rate in terms of base
     * @param allowedExchangeRateChangeUpper the max allowed change to exchange rate from an update
     * @param allowedExchangeRateChangeLower the min allowed change to exchange rate from an update
     * @param lastUpdateTimestamp the block timestamp of the last exchange rate update
     * @param isPaused whether or not this contract is paused
     * @param minimumUpdateDelayInSeconds the minimum amount of time that must pass between
     *        exchange rate updates, such that the update won't trigger the contract to be paused
     * @param platformFee the platform fee
     * @param performanceFee the performance fee
     */
    struct AccountantState {
        address payoutAddress;
        uint128 feesOwedInBase;
        uint128 totalSharesLastUpdate;
        uint96 exchangeRate;
        uint16 allowedExchangeRateChangeUpper;
        uint16 allowedExchangeRateChangeLower;
        uint64 lastUpdateTimestamp;
        bool isPaused;
        uint24 minimumUpdateDelayInSeconds;
        uint16 platformFee;
        uint16 performanceFee;
    }

    // ========================================= STATE =========================================

    /**
     * @notice Store the accountant state in 3 packed slots.
     */
    AccountantState public accountantState;

    //============================== ERRORS ===============================

    error AccountantWithRateProviders__UpperBoundTooSmall();
    error AccountantWithRateProviders__LowerBoundTooLarge();
    error AccountantWithRateProviders__PlatformFeeTooLarge();
    error AccountantWithRateProviders__PerformanceFeeTooLarge();
    error AccountantWithRateProviders__Paused();
    error AccountantWithRateProviders__ZeroFeesOwed();
    error AccountantWithRateProviders__OnlyCallableByBoringVault();
    error AccountantWithRateProviders__UpdateDelayTooLarge();

    //============================== EVENTS ===============================

    event Paused();
    event Unpaused();
    event DelayInSecondsUpdated(uint24 oldDelay, uint24 newDelay);
    event UpperBoundUpdated(uint16 oldBound, uint16 newBound);
    event LowerBoundUpdated(uint16 oldBound, uint16 newBound);
    event PlatformFeeUpdated(uint16 oldFee, uint16 newFee);
    event PerformanceFeeUpdated(uint16 oldFee, uint16 newFee);
    event PayoutAddressUpdated(address oldPayout, address newPayout);
    event ExchangeRateUpdated(uint96 oldRate, uint96 newRate, uint64 currentTime);
    event FeesClaimed(address indexed feeAsset, uint256 amount);

    //============================== IMMUTABLES ===============================

    /**
     * @notice The base asset rates are provided in.
     */
    ERC20 public immutable base;

    /**
     * @notice The decimals rates are provided in.
     */
    uint8 public immutable decimals;

    /**
     * @notice The BoringVault this accountant is working with.
     *         Used to determine share supply for fee calculation.
     */
    BoringVault public immutable vault;

    /**
     * @notice One share of the BoringVault.
     */
    uint256 internal immutable ONE_SHARE;

    constructor(
        address _primeRBAC,
        address _vault,
        address payable _payoutAddress,
        uint96 startingExchangeRate,
        uint16 allowedExchangeRateChangeUpper,
        uint16 allowedExchangeRateChangeLower,
        uint24 minimumUpdateDelayInSeconds,
        uint16 platformFee,
        uint16 performanceFee
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = BoringVault(payable(_vault));
        base = vault.asset();
        decimals = base.decimals();
        ONE_SHARE = 10 ** vault.decimals();
        accountantState = AccountantState({
            payoutAddress: _payoutAddress,
            feesOwedInBase: 0,
            totalSharesLastUpdate: uint128(vault.totalSupply()),
            exchangeRate: startingExchangeRate,
            allowedExchangeRateChangeUpper: allowedExchangeRateChangeUpper,
            allowedExchangeRateChangeLower: allowedExchangeRateChangeLower,
            lastUpdateTimestamp: uint64(block.timestamp),
            isPaused: false,
            minimumUpdateDelayInSeconds: minimumUpdateDelayInSeconds,
            platformFee: platformFee,
            performanceFee: performanceFee
        });
    }

    // ========================================= ADMIN FUNCTIONS =========================================
    /**
     * @notice Pause this contract, which prevents future calls to `updateExchangeRate`, and any safe rate
     *         calls will revert.
     * @dev Callable by MULTISIG_ROLE.
     */
    function pause() external onlyEmergencyAdmin {
        accountantState.isPaused = true;
        emit Paused();
    }

    /**
     * @notice Unpause this contract, which allows future calls to `updateExchangeRate`, and any safe rate
     *         calls will stop reverting.
     * @dev Callable by MULTISIG_ROLE.
     */
    function unpause() external onlyEmergencyAdmin {
        accountantState.isPaused = false;
        emit Unpaused();
    }

    /**
     * @notice Update the minimum time delay between `updateExchangeRate` calls.
     * @dev There are no input requirements, as it is possible the admin would want
     *      the exchange rate updated as frequently as needed.
     * @dev Callable by OWNER_ROLE.
     */
    function updateDelay(uint24 minimumUpdateDelayInSeconds) external onlyProtocolAdmin {
        if (minimumUpdateDelayInSeconds > 14 days) revert AccountantWithRateProviders__UpdateDelayTooLarge();
        uint24 oldDelay = accountantState.minimumUpdateDelayInSeconds;
        accountantState.minimumUpdateDelayInSeconds = minimumUpdateDelayInSeconds;
        emit DelayInSecondsUpdated(oldDelay, minimumUpdateDelayInSeconds);
    }

    /**
     * @notice Update the allowed upper bound change of exchange rate between `updateExchangeRateCalls`.
     * @dev Callable by OWNER_ROLE.
     */
    function updateUpper(uint16 allowedExchangeRateChangeUpper) external onlyProtocolAdmin {
        if (allowedExchangeRateChangeUpper < 1e4) revert AccountantWithRateProviders__UpperBoundTooSmall();
        uint16 oldBound = accountantState.allowedExchangeRateChangeUpper;
        accountantState.allowedExchangeRateChangeUpper = allowedExchangeRateChangeUpper;
        emit UpperBoundUpdated(oldBound, allowedExchangeRateChangeUpper);
    }

    /**
     * @notice Update the allowed lower bound change of exchange rate between `updateExchangeRateCalls`.
     * @dev Callable by OWNER_ROLE.
     */
    function updateLower(uint16 allowedExchangeRateChangeLower) external onlyProtocolAdmin {
        if (allowedExchangeRateChangeLower > 1e4) revert AccountantWithRateProviders__LowerBoundTooLarge();
        uint16 oldBound = accountantState.allowedExchangeRateChangeLower;
        accountantState.allowedExchangeRateChangeLower = allowedExchangeRateChangeLower;
        emit LowerBoundUpdated(oldBound, allowedExchangeRateChangeLower);
    }

    /**
     * @notice Update the platform fee to a new value.
     * @dev Callable by OWNER_ROLE.
     */
    function updatePlatformFee(uint16 platformFee) external onlyProtocolAdmin {
        if (platformFee > 0.2e4) revert AccountantWithRateProviders__PlatformFeeTooLarge();
        uint16 oldFee = accountantState.platformFee;
        accountantState.platformFee = platformFee;
        emit PlatformFeeUpdated(oldFee, platformFee);
    }

    /**
     * @notice Update the performance fee to a new value.
     * @dev Callable by OWNER_ROLE.
     */
    function updatePerformanceFee(uint16 performanceFee) external requiresAuth {
        if (performanceFee > 0.5e4) revert AccountantWithRateProviders__PerformanceFeeTooLarge();
        uint16 oldFee = accountantState.performanceFee;
        accountantState.performanceFee = performanceFee;
        emit PerformanceFeeUpdated(oldFee, performanceFee);
    }

    /**
     * @notice Update the payout address fees are sent to.
     * @dev Callable by OWNER_ROLE.
     */
    function updatePayoutAddress(address payoutAddress) external onlyProtocolAdmin {
        address oldPayout = accountantState.payoutAddress;
        accountantState.payoutAddress = payoutAddress;
        emit PayoutAddressUpdated(oldPayout, payoutAddress);
    }

    // ========================================= UPDATE EXCHANGE RATE/FEES FUNCTIONS =========================================

    /**
     * @notice Updates this contract exchangeRate.
     * @dev If new exchange rate is outside of accepted bounds, or if not enough time has passed, this
     *      will pause the contract, and this function will NOT calculate fees owed.
     * @dev Callable by UPDATE_EXCHANGE_RATE_ROLE.
     */
    function updateExchangeRate(uint96 newExchangeRate) external virtual requiresAuth {
        (
            bool shouldPause,
            AccountantState storage state,
            uint64 currentTime,
            uint256 currentExchangeRate,
            uint256 currentTotalShares
        ) = _beforeUpdateExchangeRate(newExchangeRate);
        if (shouldPause) {
            // Instead of reverting, pause the contract. This way the exchange rate updater is able to update the exchange rate
            // to a better value, and pause it.
            state.isPaused = true;
        } else {
            _calculateFeesOwed(state, newExchangeRate, currentExchangeRate, currentTotalShares, currentTime);
        }

        newExchangeRate = _setExchangeRate(newExchangeRate, state);
        state.totalSharesLastUpdate = uint128(currentTotalShares);
        state.lastUpdateTimestamp = currentTime;

        emit ExchangeRateUpdated(uint96(currentExchangeRate), newExchangeRate, currentTime);
    }

    /**
     * @notice Claim pending fees.
     * @dev This function must be called by the BoringVault.
     * @dev Fees are always paid in base asset.
     */
    function claimFees() external {
        if (msg.sender != address(vault)) revert AccountantWithRateProviders__OnlyCallableByBoringVault();

        AccountantState storage state = accountantState;
        if (state.isPaused) revert AccountantWithRateProviders__Paused();
        if (state.feesOwedInBase == 0) revert AccountantWithRateProviders__ZeroFeesOwed();

        uint256 feesOwed = state.feesOwedInBase;

        // Zero out fees owed.
        state.feesOwedInBase = 0;

        // Transfer base asset to payout address.
        base.safeTransferFrom(msg.sender, state.payoutAddress, feesOwed);

        emit FeesClaimed(address(base), feesOwed);
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Get this BoringVault's current rate in the base.
     */
    function getRate() public view virtual returns (uint256 rate) {
        rate = accountantState.exchangeRate;
    }

    /**
     * @notice Get this BoringVault's current rate in the base.
     * @dev Revert if paused.
     */
    function getRateSafe() external view virtual returns (uint256 rate) {
        if (accountantState.isPaused) revert AccountantWithRateProviders__Paused();
        rate = getRate();
    }

    /**
     * @notice Preview the result of an update to the exchange rate.
     * @return updateWillPause Whether the update will pause the contract.
     * @return newFeesOwedInBase The new fees owed in base.
     * @return totalFeesOwedInBase The total fees owed in base.
     */
    function previewUpdateExchangeRate(
        uint96 newExchangeRate
    ) external view virtual returns (bool updateWillPause, uint256 newFeesOwedInBase, uint256 totalFeesOwedInBase) {
        (
            bool shouldPause,
            AccountantState storage state,
            uint64 currentTime,
            uint256 currentExchangeRate,
            uint256 currentTotalShares
        ) = _beforeUpdateExchangeRate(newExchangeRate);
        updateWillPause = shouldPause;
        totalFeesOwedInBase = state.feesOwedInBase;
        if (!shouldPause) {
            uint256 platformFeesOwedInBase = _calculatePlatformFee(
                state.totalSharesLastUpdate,
                state.lastUpdateTimestamp,
                state.platformFee,
                newExchangeRate,
                currentExchangeRate,
                currentTotalShares,
                currentTime
            );

            newFeesOwedInBase = platformFeesOwedInBase;
            totalFeesOwedInBase += newFeesOwedInBase;
        }
    }

    // ========================================= INTERNAL HELPER FUNCTIONS =========================================
    /**
     * @notice Check if the new exchange rate is outside of the allowed bounds or if not enough time has passed.
     */
    function _beforeUpdateExchangeRate(
        uint96 newExchangeRate
    )
        internal
        view
        returns (
            bool shouldPause,
            AccountantState storage state,
            uint64 currentTime,
            uint256 currentExchangeRate,
            uint256 currentTotalShares
        )
    {
        state = accountantState;
        if (state.isPaused) revert AccountantWithRateProviders__Paused();
        currentTime = uint64(block.timestamp);
        currentExchangeRate = state.exchangeRate;
        currentTotalShares = vault.totalSupply();
        shouldPause =
            currentTime < state.lastUpdateTimestamp + state.minimumUpdateDelayInSeconds ||
            newExchangeRate > currentExchangeRate.mulDivDown(state.allowedExchangeRateChangeUpper, 1e4) ||
            newExchangeRate < currentExchangeRate.mulDivDown(state.allowedExchangeRateChangeLower, 1e4);
    }

    /**
     * @notice Set the exchange rate.
     */
    function _setExchangeRate(uint96 newExchangeRate, AccountantState storage state) internal virtual returns (uint96) {
        state.exchangeRate = newExchangeRate;
        return newExchangeRate;
    }

    /**
     * @notice Calculate platform fees.
     */
    function _calculatePlatformFee(
        uint128 totalSharesLastUpdate,
        uint64 lastUpdateTimestamp,
        uint16 platformFee,
        uint96 newExchangeRate,
        uint256 currentExchangeRate,
        uint256 currentTotalShares,
        uint64 currentTime
    ) internal view returns (uint256 platformFeesOwedInBase) {
        uint256 shareSupplyToUse = currentTotalShares;
        // Use the minimum between current total supply and total supply for last update.
        if (totalSharesLastUpdate < shareSupplyToUse) {
            shareSupplyToUse = totalSharesLastUpdate;
        }

        // Determine platform fees owned.
        if (platformFee > 0) {
            uint256 timeDelta = currentTime - lastUpdateTimestamp;
            uint256 minimumAssets = newExchangeRate > currentExchangeRate
                ? shareSupplyToUse.mulDivDown(currentExchangeRate, ONE_SHARE)
                : shareSupplyToUse.mulDivDown(newExchangeRate, ONE_SHARE);
            uint256 platformFeesAnnual = minimumAssets.mulDivDown(platformFee, 1e4);
            platformFeesOwedInBase = platformFeesAnnual.mulDivDown(timeDelta, 365 days);
        }
    }

    /**
     * @notice Calculate performance fees.
     */
    function _calculatePerformanceFee(
        uint96 newExchangeRate,
        uint256 shareSupplyToUse,
        uint96 datum,
        uint16 performanceFee
    ) internal view returns (uint256 performanceFeesOwedInBase, uint256 yieldEarned) {
        uint256 changeInExchangeRate = newExchangeRate - datum;
        yieldEarned = changeInExchangeRate.mulDivDown(shareSupplyToUse, ONE_SHARE);
        if (performanceFee > 0) {
            performanceFeesOwedInBase = yieldEarned.mulDivDown(performanceFee, 1e4);
        }
    }

    /**
     * @notice Calculate fees owed in base.
     * @dev This function will update the highwater mark if the new exchange rate is higher.
     */
    function _calculateFeesOwed(
        AccountantState storage state,
        uint96 newExchangeRate,
        uint256 currentExchangeRate,
        uint256 currentTotalShares,
        uint64 currentTime
    ) internal virtual {
        // Only update fees if we are not paused.
        // Update fee accounting.
        uint256 newFeesOwedInBase = _calculatePlatformFee(
            state.totalSharesLastUpdate,
            state.lastUpdateTimestamp,
            state.platformFee,
            newExchangeRate,
            currentExchangeRate,
            currentTotalShares,
            currentTime
        );

        state.feesOwedInBase += uint128(newFeesOwedInBase);
    }
}

project/contracts/core/AccountantWithYie.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {IRateProvider} from "../interfaces/IRateProvider.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {Auth, Authority} from "solmate/src/auth/Auth.sol";
import {AccountantWithRateProviders} from "./AccountantWithRateProviders.sol";

contract AccountantWithYieldStreaming is AccountantWithRateProviders {
    using FixedPointMathLib for uint256;
    using SafeTransferLib for ERC20;

    // ========================================= STRUCTS =========================================
    /**
     * @notice Stores the state variables related to yield vesting and share price tracking
     * @dev lastSharePrice The most recent share price
     * @dev vestingGains The total amount of yield being streamed for this period
     * @dev lastVestingUpdate The last time a yield update was posted
     * @dev startVestingTime The start time for the yield streaming period
     * @dev endVestingTime The end time for the yield streaming period
     */
    struct VestingState {
        uint128 lastSharePrice;
        uint128 vestingGains;
        uint128 lastVestingUpdate;
        uint64 startVestingTime;
        uint64 endVestingTime;
    }

    struct SupplyObservation {
        uint256 cumulativeSupply;
        uint256 cumulativeSupplyLast;
        uint256 lastUpdateTimestamp;
    }

    // ========================================= STATE =========================================

    /**
     * @notice Store the vesting state in 2 packed slots.
     */
    VestingState public vestingState;

    /**
     * @notice Store the supply observation state in 3 slots.
     */
    SupplyObservation public supplyObservation;

    /**
     * @notice The minimum amount of time a yield update is required to vest to be posted to the vault
     * @dev set to sane default but configurable by ADMIN_ROLE
     */
    uint64 public minimumVestingTime = 1 days;

    /**
     * @notice The maximum amount of time a yield update can vest to be posted to the vault
     * @dev set to sane default but configurable by ADMIN_ROLE
     */
    uint64 public maximumVestingTime = 7 days;

    /**
     * @notice The maximum amount a yield vest can be > old supply
     * @dev recorded in bps (maxDeviationYield / 10_000)
     */
    uint32 public maxDeviationYield = 500;

    /**
     * @notice The last time any vesting function was called
     * @dev applies to vestYield
     */
    uint64 public lastStrategistUpdateTimestamp;

    //============================== ERRORS ===============================

    error AccountantWithYieldStreaming__UpdateExchangeRateNotSupported();
    error AccountantWithYieldStreaming__DurationExceedsMaximum();
    error AccountantWithYieldStreaming__DurationUnderMinimum();
    error AccountantWithYieldStreaming__NotEnoughTimePassed();
    error AccountantWithYieldStreaming__ZeroYieldUpdate();
    error AccountantWithYieldStreaming__MaxDeviationYieldExceeded();

    //============================== EVENTS ===============================

    event YieldRecorded(uint256 amountAdded, uint64 endVestingTime);
    event ExchangeRateUpdated(uint256 newExchangeRate);
    event MaximumVestDurationUpdated(uint64 newMaximum);
    event MinimumVestDurationUpdated(uint64 newMinimum);
    event MaximumDeviationYieldUpdated(uint64 newMaximum);

    constructor(
        address _primeRBAC,
        address _vault,
        address payoutAddress,
        uint96 startingExchangeRate,
        uint16 allowedExchangeRateChangeUpper,
        uint16 allowedExchangeRateChangeLower,
        uint24 minimumUpdateDelayInSeconds,
        uint16 platformFee,
        uint16 performanceFee
    )
        AccountantWithRateProviders(
            _primeRBAC,
            _vault,
            payable(payoutAddress),
            startingExchangeRate,
            allowedExchangeRateChangeUpper,
            allowedExchangeRateChangeLower,
            minimumUpdateDelayInSeconds,
            platformFee,
            performanceFee
        )
    {
        //initialize vesting state
        vestingState.lastSharePrice = startingExchangeRate;
        vestingState.vestingGains = 0;
        vestingState.lastVestingUpdate = uint128(block.timestamp);
        vestingState.startVestingTime = uint64(block.timestamp);
        vestingState.endVestingTime = uint64(block.timestamp);

        //initialize supply observations
        supplyObservation.cumulativeSupply = 0;
        supplyObservation.cumulativeSupplyLast = 0;
        supplyObservation.lastUpdateTimestamp = uint128(block.timestamp);

        //initialize strategist update time to deploy time so first posts are valid
        lastStrategistUpdateTimestamp = uint64(block.timestamp);
    }

    // ========================================= UPDATE EXCHANGE RATE/FEES FUNCTIONS =========================================

    /**
     * @notice Record new yield to be vested over a duration
     * @param yieldAmount The amount of yield earned
     * @param duration The period over which to vest this yield
     * @notice callable by the STRATEGIST role
     * @dev `yieldAmount` should be denominated in the BASE ASSET
     */
    function vestYield(uint256 yieldAmount, uint256 duration) external requiresAuth {
        if (accountantState.isPaused) revert AccountantWithRateProviders__Paused();

        if (duration > uint256(maximumVestingTime)) revert AccountantWithYieldStreaming__DurationExceedsMaximum();
        if (duration < uint256(minimumVestingTime)) revert AccountantWithYieldStreaming__DurationUnderMinimum();
        if (yieldAmount == 0) revert AccountantWithYieldStreaming__ZeroYieldUpdate();
        //only check if there's an active vest
        if (vestingState.vestingGains > 0) {
            if (block.timestamp < lastStrategistUpdateTimestamp + accountantState.minimumUpdateDelayInSeconds) {
                revert AccountantWithYieldStreaming__NotEnoughTimePassed();
            }
        }

        //update the exchange rate, then validate if everything checks out
        _updateExchangeRate();

        //use TWAS to validate the yield amount:
        uint256 averageSupply = _getTWAS();
        uint256 _totalAssets = averageSupply.mulDivDown(vestingState.lastSharePrice, ONE_SHARE);
        uint256 dailyYieldAmount = yieldAmount.mulDivDown(1 days, duration);
        uint256 dailyYieldBps = dailyYieldAmount.mulDivDown(10_000, _totalAssets);

        if (dailyYieldBps > maxDeviationYield) {
            // maxDeviationYield is in bps
            revert AccountantWithYieldStreaming__MaxDeviationYieldExceeded();
        }

        //update the cumulative supply checkpoint
        supplyObservation.cumulativeSupplyLast = supplyObservation.cumulativeSupply;

        //strategists should account for any unvested yield they want, gives more flexibility in posting pnl updates
        vestingState.vestingGains = uint128(yieldAmount);

        //update vesting timestamps
        vestingState.startVestingTime = uint64(block.timestamp);
        vestingState.endVestingTime = uint64(block.timestamp + duration);

        //update state timestamp
        lastStrategistUpdateTimestamp = uint64(block.timestamp);

        emit YieldRecorded(yieldAmount, vestingState.endVestingTime);
    }

    /**
     * @dev calling this moves any vested gains to be calculated into the current share price
     */
    function updateExchangeRate() external requiresAuth {
        _updateExchangeRate();
    }

    /**
     * @notice Override updateExchangeRate to revert if called accidentally
     */
    function updateExchangeRate(uint96 /*newExchangeRate*/) external view override requiresAuth {
        revert AccountantWithYieldStreaming__UpdateExchangeRateNotSupported();
    }

    /**
     * @notice Updates startVestingTime timestamp
     * @dev Callable by TELLER
     */
    function setFirstDepositTimestamp() external requiresAuth {
        vestingState.startVestingTime = uint64(block.timestamp);
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Update the maximum vesting time to a new value.
     * @dev Callable by OWNER_ROLE.
     */
    function updateMaximumVestDuration(uint64 newMaximum) external requiresAuth {
        maximumVestingTime = newMaximum;
        emit MaximumVestDurationUpdated(newMaximum);
    }

    /**
     * @notice Update the minimum vesting time to a new value.
     * @dev Callable by OWNER_ROLE.
     */
    function updateMinimumVestDuration(uint64 newMinimum) external requiresAuth {
        minimumVestingTime = newMinimum;
        emit MinimumVestDurationUpdated(newMinimum);
    }

    /**
     * @notice Update the maximum deviation yield
     * @dev Callable by OWNER_ROLE.
     */
    function updateMaximumDeviationYield(uint32 newMaximum) external requiresAuth {
        maxDeviationYield = newMaximum;
        emit MaximumDeviationYieldUpdated(newMaximum);
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Returns the rate for one share at current block based on amount of gains that are vested and have vested
     * @dev linear interpolation between current timestamp and `endVestingTime`
     */
    function getRate() public view override returns (uint256 rate) {
        uint256 currentShares = vault.totalSupply();
        if (currentShares == 0) {
            return rate = vestingState.lastSharePrice; //startingExchangeRate
        }
        rate = totalAssets().mulDivDown(ONE_SHARE, currentShares);
    }

    /**
     * @notice Returns the safe rate for one share
     * @dev Rerverts if the the accountant is paused
     */
    function getRateSafe() external view override returns (uint256 rate) {
        if (accountantState.isPaused) revert AccountantWithRateProviders__Paused();
        return rate = getRate();
    }

    /**
     * @notice Returns the amount of yield that has already vested based on the current block and `vestingGains`
     */
    function getPendingVestingGains() public view returns (uint256 amountVested) {
        uint256 currentTime = block.timestamp;

        //if we're past the end of vesting, all remaining gains have vested
        if (currentTime >= vestingState.endVestingTime) {
            return vestingState.vestingGains; // Return ALL remaining unvested gains
        }

        //if no gains to vest
        if (vestingState.vestingGains == 0) {
            return 0;
        }

        //time that has passed since last update
        uint256 timeSinceLastUpdate = currentTime - vestingState.lastVestingUpdate;

        //total remaining vesting period when we last updated
        uint256 totalRemainingTime = vestingState.endVestingTime - vestingState.lastVestingUpdate;

        //vest it linearly over the remaining time
        //return amountVested = (vestingState.vestingGains * timeSinceLastUpdate) / totalRemainingTime;
        return amountVested = uint256(vestingState.vestingGains).mulDivDown(timeSinceLastUpdate, totalRemainingTime);
    }

    /**
     * @notice Returns the amount of yield that has yet to vest based on the current block and `vestingGains`
     */
    function getPendingUnvestedGains() external view returns (uint256) {
        return vestingState.vestingGains - getPendingVestingGains();
    }

    /**
     * @notice Calculate TWAS since last vest
     */
    function _getTWAS() internal view returns (uint256) {
        //handle first yield event
        if (supplyObservation.cumulativeSupply == 0) {
            return vault.totalSupply();
        }

        uint64 timeSinceLastVest = uint64(block.timestamp) - vestingState.startVestingTime;

        if (timeSinceLastVest == 0) {
            return vault.totalSupply(); // If no time passed, return current supply
        }

        // TWAS = (current cumulative - last vest cumulative) / time elapsed
        uint256 cumulativeDelta = supplyObservation.cumulativeSupply - supplyObservation.cumulativeSupplyLast;
        return cumulativeDelta / timeSinceLastVest;
    }

    /**
     * @notice Returns the total assets in the vault at current timestamp
     * @dev Includes any gains that have already vested for this period
     */
    function totalAssets() public view returns (uint256) {
        uint256 currentShares = vault.totalSupply();
        return uint256(vestingState.lastSharePrice).mulDivDown(currentShares, ONE_SHARE) + getPendingVestingGains();
    }

    /**
     * @notice Returns the current version of the accountant
     */
    function version() external pure returns (string memory) {
        return "V0.1";
    }

    /**
     * @notice Override previewUpdateExchangeRate to revert if called accidentally
     */
    function previewUpdateExchangeRate(
        uint96 /*newExchangeRate*/
    )
        external
        view
        override
        requiresAuth
        returns (bool, /*updateWillPause*/ uint256, /*newFeesOwedInBase*/ uint256 /*totalFeesOwedInBase*/)
    {
        revert AccountantWithYieldStreaming__UpdateExchangeRateNotSupported();
    }

    // ========================================= INTERNAL HELPER FUNCTIONS =========================================

    /**
     * @dev calling this moves any vested gains to be calculated into the current share price
     */
    function _updateExchangeRate() internal {
        AccountantState storage state = accountantState;
        if (state.isPaused) revert AccountantWithRateProviders__Paused();
        _updateCumulative();

        //calculate how much has vested since `lastVestingUpdate`
        uint256 newlyVested = getPendingVestingGains();

        uint256 currentShares = vault.totalSupply();
        if (newlyVested > 0) {
            // update the share price w/o reincluding the pending gains (done in `newlyVested`)
            uint256 _totalAssets = uint256(vestingState.lastSharePrice).mulDivDown(currentShares, ONE_SHARE);
            vestingState.lastSharePrice = uint128((_totalAssets + newlyVested).mulDivDown(ONE_SHARE, currentShares));

            //move vested amount from pending to realized
            vestingState.vestingGains -= uint128(newlyVested); // remove from pending
        }

        //sync fee variables
        _collectFees();

        //always update timestamp
        vestingState.lastVestingUpdate = uint128(block.timestamp); // update timestamp

        state.totalSharesLastUpdate = uint128(currentShares);

        emit ExchangeRateUpdated(vestingState.lastSharePrice);
    }

    /**
     * @notice Updates the cumulative supply tracking
     * @dev Called before any supply changes and before TWAS calculations
     */
    function _updateCumulative() internal {
        uint256 currentTime = block.timestamp;
        uint256 timeElapsed = currentTime - supplyObservation.lastUpdateTimestamp;

        if (timeElapsed > 0) {
            //add (current supply * time elapsed) to accumulator
            supplyObservation.cumulativeSupply += vault.totalSupply() * timeElapsed;
            supplyObservation.lastUpdateTimestamp = currentTime;
        }
    }

    /**
     * @notice Call this before share price increases to collect fees
     */
    function _collectFees() internal {
        AccountantState storage state = accountantState;
        uint256 currentTotalShares = vault.totalSupply();
        uint64 currentTime = uint64(block.timestamp);

        //calculate fees using function inherited from `AccountantWithRateProviders`
        _calculateFeesOwed(
            state,
            uint96(vestingState.lastSharePrice),
            state.exchangeRate,
            currentTotalShares,
            currentTime
        );

        state.exchangeRate = uint96(vestingState.lastSharePrice);
        state.lastUpdateTimestamp = currentTime;
    }
}

project/contracts/core/BoringVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";

import {PrimeAuth} from "../auth/PrimeAuth.sol";

contract BoringVault is ERC20, PrimeAuth, ERC721Holder, ERC1155Holder {
    using Address for address;
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;

    // ========================================= STATE =========================================

    /**
     * @notice The underlying asset token for this vault
     */
    ERC20 public immutable asset;

    /**
     * @notice Contract responsible for implementing `beforeUpdate`.
     */
    IBeforeUpdateHook public beforeUpdateHook;

    //============================== EVENTS ===============================

    event Enter(address indexed from, address indexed asset, uint256 amount, address indexed to, uint256 shares);
    event Exit(address indexed to, address indexed asset, uint256 amount, address indexed from, uint256 shares);

    //============================== ERRORS ===============================

    error BoringVault__InvalidAsset();

    //============================== CONSTRUCTOR ===============================

    constructor(
        address _primeRBAC,
        address _authority,
        string memory _name,
        string memory _symbol,
        address _asset
    ) ERC20(_name, _symbol, ERC20(_asset).decimals()) PrimeAuth(_primeRBAC, _authority) {
        asset = ERC20(_asset);
    }

    //============================== MANAGE ===============================

    /**
     * @notice Allows manager to make an arbitrary function call from this contract.
     * @dev Callable by MANAGER_ROLE.
     */
    function manage(
        address target,
        bytes calldata data,
        uint256 value
    ) external requiresAuth returns (bytes memory result) {
        result = target.functionCallWithValue(data, value);
    }

    /**
     * @notice Allows manager to make arbitrary function calls from this contract.
     * @dev Callable by MANAGER_ROLE.
     */
    function bulkManage(
        address[] calldata targets,
        bytes[] calldata data,
        uint256[] calldata values
    ) external requiresAuth returns (bytes[] memory results) {
        uint256 targetsLength = targets.length;
        results = new bytes[](targetsLength);
        for (uint256 i; i < targetsLength; ++i) {
            results[i] = targets[i].functionCallWithValue(data[i], values[i]);
        }
    }

    //============================== ENTER ===============================

    /**
     * @notice Allows minter to mint shares, in exchange for assets.
     * @dev If assetAmount is zero, no assets are transferred in.
     * @dev Callable by MINTER_ROLE.
     */
    function enter(address from, uint256 assetAmount, address to, uint256 shareAmount) external requiresAuth {
        // Transfer assets in
        if (assetAmount > 0) asset.safeTransferFrom(from, address(this), assetAmount);

        // Update rewards BEFORE minting (with old balances)
        _callBeforeUpdate(address(0), to, shareAmount, msg.sender);

        // Mint shares.
        _mint(to, shareAmount);

        emit Enter(from, address(asset), assetAmount, to, shareAmount);
    }

    //============================== EXIT ===============================

    /**
     * @notice Allows burner to burn shares, in exchange for assets.
     * @dev If assetAmount is zero, no assets are transferred out.
     * @dev Callable by BURNER_ROLE.
     */
    function exit(address to, uint256 assetAmount, address from, uint256 shareAmount) external requiresAuth {
        // Update rewards BEFORE burning (with old balances)
        _callBeforeUpdate(from, address(0), shareAmount, msg.sender);

        // Burn shares.
        _burn(from, shareAmount);

        // Transfer assets out.
        if (assetAmount > 0) asset.safeTransfer(to, assetAmount);

        emit Exit(to, address(asset), assetAmount, from, shareAmount);
    }

    //============================== BEFORE UPDATE HOOK ===============================

    /**
     * @notice Sets the before update hook (e.g., Distributor).
     * @notice If set to zero address, the hook is disabled.
     * @dev Callable by OWNER_ROLE.
     */
    function setBeforeUpdateHook(address _hook) external onlyProtocolAdmin {
        beforeUpdateHook = IBeforeUpdateHook(_hook);
    }

    /**
     * @notice Call `beforeUpdate` hook BEFORE balance changes.
     */
    function _callBeforeUpdate(address from, address to, uint256 amount, address operator) internal {
        if (address(beforeUpdateHook) != address(0)) beforeUpdateHook.beforeUpdate(from, to, amount, operator);
    }

    function transfer(address to, uint256 amount) public override returns (bool) {
        _callBeforeUpdate(msg.sender, to, amount, msg.sender);
        return super.transfer(to, amount);
    }

    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        _callBeforeUpdate(from, to, amount, msg.sender);
        return super.transferFrom(from, to, amount);
    }

    //============================== RECEIVE ===============================

    receive() external payable {}
}

project/contracts/core/DelayedWithdraw.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {WETH} from "solmate/src/tokens/WETH.sol";
import {BoringVault} from "./BoringVault.sol";
import {AccountantWithRateProviders} from "./AccountantWithRateProviders.sol";
import {TellerWithMultiAssetSupport} from "./TellerWithMultiAssetSupport.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";

import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {IPausable} from "../interfaces/IPausable.sol";

import "../auth/PrimeAuth.sol";

contract DelayedWithdraw is PrimeAuth, ReentrancyGuard, IPausable {
    using SafeTransferLib for BoringVault;
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;

    // ========================================= STRUCTS =========================================

    /**
     * @param allowWithdraws Whether or not withdrawals are allowed for this asset.
     * @param withdrawDelay The delay in seconds before a requested withdrawal can be completed.
     * @param outstandingShares The total number of shares that are currently outstanding for an asset.
     * @param withdrawFee The fee that is charged when a withdrawal is completed.
     */
    struct WithdrawAsset {
        bool allowWithdraws;
        uint32 withdrawDelay;
        uint128 outstandingShares;
        uint16 withdrawFee;
    }

    /**
     * @param allowThirdPartyToComplete Whether or not a 3rd party can complete a withdraw on behalf of a user.
     * @param maturity The time at which the withdrawal can be completed.
     * @param shares The number of shares that are requested to be withdrawn.
     * @param exchangeRateAtTimeOfRequest The exchange rate at the time of the request.
     */
    struct WithdrawRequest {
        bool allowThirdPartyToComplete;
        uint40 maturity;
        uint96 shares;
        uint96 exchangeRateAtTimeOfRequest;
    }

    // ========================================= CONSTANTS =========================================

    /**
     * @notice The largest withdraw fee that can be set.
     */
    uint16 internal constant MAX_WITHDRAW_FEE = 0.2e4;

    // ========================================= STATE =========================================

    /**
     * @notice The address that receives the fee when a withdrawal is completed.
     */
    address public feeAddress;

    /**
     * @notice Used to pause calls to `requestWithdraw`, and `completeWithdraw`.
     */
    bool public isPaused;

    /**
     * @notice Whether or not the contract should pull funds from the Boring Vault when completing a withdrawal,
     *         or use funds the BoringVault has previously sent to this contract.
     */
    bool public pullFundsFromVault;

    /**
     * @notice The withdrawal settings for the asset.
     */
    WithdrawAsset public withdrawAsset;

    /**
     * @notice The mapping of users to their withdrawal requests.
     */
    mapping(address => WithdrawRequest) public withdrawRequests;

    //============================== ERRORS ===============================

    error DelayedWithdraw__WithdrawFeeTooHigh();
    error DelayedWithdraw__AlreadySetup();
    error DelayedWithdraw__WithdrawsNotAllowed();
    error DelayedWithdraw__WithdrawNotMatured();
    error DelayedWithdraw__NoSharesToWithdraw();
    error DelayedWithdraw__BadAddress();
    error DelayedWithdraw__ThirdPartyCompletionNotAllowed();
    error DelayedWithdraw__Paused();
    error DelayedWithdraw__CallerNotBoringVault();
    error DelayedWithdraw__CannotWithdrawBoringToken();

    //============================== EVENTS ===============================

    event WithdrawRequested(address indexed account, ERC20 indexed asset, uint96 shares, uint40 maturity);
    event WithdrawCancelled(address indexed account, ERC20 indexed asset, uint96 shares);
    event WithdrawCompleted(address indexed account, ERC20 indexed asset, uint256 shares, uint256 assets);
    event FeeAddressSet(address newFeeAddress);
    event SetupWithdrawalsInAsset(address indexed asset, uint64 withdrawDelay, uint16 withdrawFee);
    event WithdrawDelayUpdated(address indexed asset, uint32 newWithdrawDelay);
    event WithdrawFeeUpdated(address indexed asset, uint16 newWithdrawFee);
    event WithdrawalsStopped(address indexed asset);
    event ThirdPartyCompletionChanged(address indexed account, ERC20 indexed asset, bool allowed);
    event Paused();
    event Unpaused();
    event PullFundsFromVaultUpdated(bool _pullFundsFromVault);

    //============================== IMMUTABLES ===============================

    /**
     * @notice The accountant contract that is used to get the exchange rate of assets.
     */
    AccountantWithRateProviders internal immutable accountant;

    /**
     * @notice The teller contract that users are depositing to.
     */
    TellerWithMultiAssetSupport internal immutable teller;

    /**
     * @notice The BoringVault contract that users are withdrawing from.
     */
    BoringVault public immutable boringVault;

    /**
     * @notice The asset that can be withdrawn from the vault.
     */
    ERC20 public immutable asset;

    /**
     * @notice Constant that represents 1 share.
     */
    uint256 internal immutable ONE_SHARE;

    constructor(
        address _primeRBAC,
        address _vault,
        address _accountant,
        address _teller,
        address _payoutAddress
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        accountant = AccountantWithRateProviders(_accountant);
        teller = TellerWithMultiAssetSupport(_teller);
        boringVault = BoringVault(payable(_vault));
        asset = ERC20(address(boringVault.asset()));
        ONE_SHARE = 10 ** boringVault.decimals();
        if (_payoutAddress == address(0)) revert DelayedWithdraw__BadAddress();
        feeAddress = _payoutAddress;
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Pause this contract, which prevents future calls to `manageVaultWithMerkleVerification`.
     * @dev Callable by MULTISIG_ROLE.
     */
    function pause() external requiresAuth {
        isPaused = true;
        emit Paused();
    }

    /**
     * @notice Unpause this contract, which allows future calls to `manageVaultWithMerkleVerification`.
     * @dev Callable by MULTISIG_ROLE.
     */
    function unpause() external requiresAuth {
        isPaused = false;
        emit Unpaused();
    }

    /**
     * @notice Stops withdrawals.
     * @dev Callable by MULTISIG_ROLE.
     */
    function stopWithdrawals() external requiresAuth {
        if (!withdrawAsset.allowWithdraws) revert DelayedWithdraw__WithdrawsNotAllowed();

        withdrawAsset.allowWithdraws = false;

        emit WithdrawalsStopped(address(asset));
    }

    /**
     * @notice Sets up the withdrawal settings.
     * @dev Callable by OWNER_ROLE.
     */
    function setupWithdraw(uint32 withdrawDelay, uint16 withdrawFee) external onlyProtocolAdmin {
        if (withdrawFee > MAX_WITHDRAW_FEE) revert DelayedWithdraw__WithdrawFeeTooHigh();

        if (withdrawAsset.allowWithdraws) revert DelayedWithdraw__AlreadySetup();
        withdrawAsset.allowWithdraws = true;
        withdrawAsset.withdrawDelay = withdrawDelay;
        withdrawAsset.withdrawFee = withdrawFee;

        emit SetupWithdrawalsInAsset(address(asset), withdrawDelay, withdrawFee);
    }

    /**
     * @notice Changes the withdraw delay.
     * @dev Callable by MULTISIG_ROLE.
     */
    function changeWithdrawDelay(uint32 withdrawDelay) external requiresAuth {
        if (!withdrawAsset.allowWithdraws) revert DelayedWithdraw__WithdrawsNotAllowed();

        withdrawAsset.withdrawDelay = withdrawDelay;

        emit WithdrawDelayUpdated(address(asset), withdrawDelay);
    }

    /**
     * @notice Changes the withdraw fee.
     * @dev Callable by OWNER_ROLE.
     */
    function changeWithdrawFee(uint16 withdrawFee) external requiresAuth {
        if (!withdrawAsset.allowWithdraws) revert DelayedWithdraw__WithdrawsNotAllowed();

        if (withdrawFee > MAX_WITHDRAW_FEE) revert DelayedWithdraw__WithdrawFeeTooHigh();

        withdrawAsset.withdrawFee = withdrawFee;

        emit WithdrawFeeUpdated(address(asset), withdrawFee);
    }

    /**
     * @notice Changes the fee address.
     * @dev Callable by STRATEGIST_MULTISIG_ROLE.
     */
    function setFeeAddress(address _feeAddress) external requiresAuth {
        if (_feeAddress == address(0)) revert DelayedWithdraw__BadAddress();
        feeAddress = _feeAddress;

        emit FeeAddressSet(_feeAddress);
    }

    /**
     * @notice Cancels a user's withdrawal request.
     * @dev Callable by MULTISIG_ROLE, and STRATEGIST_MULTISIG_ROLE.
     */
    function cancelUserWithdraw(address user) external requiresAuth {
        _cancelWithdraw(user);
    }

    /**
     * @notice Completes a user's withdrawal request.
     * @dev Admins can complete requests even if they are outside the completion window.
     * @dev Callable by MULTISIG_ROLE, and STRATEGIST_MULTISIG_ROLE.
     */
    function completeUserWithdraw(address user) external requiresAuth returns (uint256 assetsOut) {
        WithdrawRequest storage req = withdrawRequests[user];
        assetsOut = _completeWithdraw(user, req);
    }

    /**
     * @notice Changes the global setting for whether or not to pull funds from the vault when completing a withdrawal.
     * @dev Callable by OWNER_ROLE.
     */
    function setPullFundsFromVault(bool _pullFundsFromVault) external onlyProtocolAdmin {
        pullFundsFromVault = _pullFundsFromVault;

        emit PullFundsFromVaultUpdated(_pullFundsFromVault);
    }

    /**
     * @notice Withdraws a non boring token from the contract.
     * @dev Callable by BoringVault.
     * @dev Eventhough withdrawing the BoringVault share from this contract requires
     *      a malicious leaf in the merkle tree, we explicitly revert if `token`
     *      is the BoringVault.
     * @dev For future reference if this function selector is ever changed, the
     *      associated function selector must be updated in `BaseDecoderAndSanitizer.sol`.
     */
    function withdrawNonBoringToken(ERC20 token, uint256 amount) external {
        if (msg.sender != address(boringVault)) revert DelayedWithdraw__CallerNotBoringVault();
        if (address(token) == address(boringVault)) revert DelayedWithdraw__CannotWithdrawBoringToken();

        if (amount == type(uint256).max) {
            amount = token.balanceOf(address(this));
        }

        token.safeTransfer(address(boringVault), amount);
    }

    // ========================================= PUBLIC FUNCTIONS =========================================

    /**
     * @notice Allows a user to set whether or not a 3rd party can complete withdraws on behalf of them.
     */
    function setAllowThirdPartyToComplete(bool allow) external requiresAuth {
        withdrawRequests[msg.sender].allowThirdPartyToComplete = allow;

        emit ThirdPartyCompletionChanged(msg.sender, asset, allow);
    }

    /**
     * @notice Requests a withdrawal of shares.
     * @dev Publicly callable.
     */
    function requestWithdraw(uint96 shares, bool allowThirdPartyToComplete) external requiresAuth nonReentrant {
        if (isPaused) revert DelayedWithdraw__Paused();
        if (!withdrawAsset.allowWithdraws) revert DelayedWithdraw__WithdrawsNotAllowed();

        boringVault.safeTransferFrom(msg.sender, address(this), shares);

        withdrawAsset.outstandingShares += shares;

        WithdrawRequest storage req = withdrawRequests[msg.sender];

        req.shares += shares;
        uint40 maturity = uint40(block.timestamp + withdrawAsset.withdrawDelay);
        req.maturity = maturity;
        req.exchangeRateAtTimeOfRequest = uint96(accountant.getRateSafe());
        req.allowThirdPartyToComplete = allowThirdPartyToComplete;

        emit WithdrawRequested(msg.sender, asset, shares, maturity);
    }

    /**
     * @notice Cancels msg.sender's withdrawal request.
     * @dev Publicly callable.
     */
    function cancelWithdraw() external requiresAuth nonReentrant {
        _cancelWithdraw(msg.sender);
    }

    /**
     * @notice Completes a user's withdrawal request.
     * @dev Publicly callable. No time limit after maturity.
     */
    function completeWithdraw(address account) external requiresAuth nonReentrant returns (uint256 assetsOut) {
        if (isPaused) revert DelayedWithdraw__Paused();
        WithdrawRequest storage req = withdrawRequests[account];

        if (msg.sender != account && !req.allowThirdPartyToComplete) {
            revert DelayedWithdraw__ThirdPartyCompletionNotAllowed();
        }
        assetsOut = _completeWithdraw(account, req);
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Helper function to view the outstanding withdraw debt.
     */
    function viewOutstandingDebt() public view returns (uint256 debt) {
        uint256 rate = accountant.getRateSafe();

        debt = rate.mulDivDown(withdrawAsset.outstandingShares, ONE_SHARE);
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Internal helper function that implements shared logic for cancelling a user's withdrawal request.
     */
    function _cancelWithdraw(address account) internal {
        // We do not check if `asset` is allowed, to handle edge cases where the asset is no longer allowed.

        WithdrawRequest storage req = withdrawRequests[account];
        uint96 shares = req.shares;
        if (shares == 0) revert DelayedWithdraw__NoSharesToWithdraw();
        withdrawAsset.outstandingShares -= shares;
        req.shares = 0;
        boringVault.safeTransfer(account, shares);

        emit WithdrawCancelled(account, asset, shares);
    }

    /**
     * @notice Internal helper function that implements shared logic for completing a user's withdrawal request.
     */
    function _completeWithdraw(address account, WithdrawRequest storage req) internal returns (uint256 assetsOut) {
        if (!withdrawAsset.allowWithdraws) revert DelayedWithdraw__WithdrawsNotAllowed();

        if (block.timestamp < req.maturity) revert DelayedWithdraw__WithdrawNotMatured();
        if (req.shares == 0) revert DelayedWithdraw__NoSharesToWithdraw();

        uint256 shares = req.shares;

        // Safe to cast shares to a uint128 since req.shares is constrained to be less than 2^96.
        withdrawAsset.outstandingShares -= uint128(shares);

        if (withdrawAsset.withdrawFee > 0) {
            // Handle withdraw fee.
            uint256 fee = uint256(shares).mulDivDown(withdrawAsset.withdrawFee, 1e4);
            shares -= fee;

            // Transfer fee to feeAddress.
            boringVault.safeTransfer(feeAddress, fee);
        }

        // Calculate assets out using exchange rate at time of request (shares are locked and no longer earn rewards)
        assetsOut = shares.mulDivDown(req.exchangeRateAtTimeOfRequest, ONE_SHARE);

        req.shares = 0;

        // if (pullFundsFromVault) {
        //     // Burn shares and transfer assets to user.
        //     boringVault.exit(account, asset, assetsOut, address(this), shares);
        // } else {
        //     // Burn shares.
        //     boringVault.exit(account, asset, 0, address(this), shares);
        //     // Transfer assets to user.
        //     asset.safeTransfer(account, assetsOut);
        // }
        teller.bulkWithdraw(shares, assetsOut, account);

        emit WithdrawCompleted(account, asset, shares, assetsOut);
    }
}

project/contracts/core/Distributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {PrimeAuth} from "../auth/PrimeAuth.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";
import {BoringVault} from "./BoringVault.sol";

/**
 * @title Distributor
 * @author Prime Vaults
 * @notice A multi-reward distributor contract integrated with PrimeVault
 * @dev Automatically tracks vault share balances without requiring explicit staking
 * Users earn multiple reward tokens based on their vault share balance
 * Integrates as a IBeforeUpdateHook to update rewards on every vault transfer
 */
contract Distributor is PrimeAuth, ReentrancyGuard, IBeforeUpdateHook {
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;

    // ========================================= STRUCTS =========================================

    struct RewardData {
        address rewardsDistributor;
        uint256 rewardsDuration;
        uint256 periodFinish;
        uint256 rewardRate;
        uint256 lastUpdateTime;
        uint256 rewardPerTokenStored;
    }

    // ========================================= STATE VARIABLES =========================================

    /// @notice The vault contract (reads balances directly)
    ERC20 public immutable vault;

    /// @notice Mapping from reward token address to its reward data
    mapping(address => RewardData) public rewardData;

    /// @notice Array of all reward token addresses
    address[] public rewardTokens;

    /// @notice user -> reward token -> amount of reward per token paid
    mapping(address => mapping(address => uint256)) public userRewardPerTokenPaid;

    /// @notice user -> reward token -> accumulated rewards
    mapping(address => mapping(address => uint256)) public rewards;

    /// @notice Whether the contract is paused
    bool public paused;

    // ========================================= EVENTS =========================================

    event RewardAdded(uint256 reward);
    event RewardPaid(address indexed user, address indexed rewardsToken, uint256 reward);
    event RewardsDurationUpdated(address token, uint256 newDuration);
    event Recovered(address token, uint256 amount);
    event PausedUpdated(bool isPaused);

    // ========================================= ERRORS =========================================

    error Distributor__AlreadyAdded();
    error Distributor__NotAuthorized();
    error Distributor__RewardPeriodActive();
    error Distributor__ZeroDuration();
    error Distributor__CannotWithdrawRewardToken();
    error Distributor__Paused();

    // ========================================= CONSTRUCTOR =========================================

    /**
     * @notice Initializes the Distributor contract
     * @param _primeRBAC The PrimeRBAC contract for authentication
     * @param _vault The vault contract (source of share balances)
     */
    constructor(
        address _primeRBAC,
        address _vault
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = ERC20(_vault);
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Add a new reward token to the contract
     * @param _rewardsToken The address of the reward token
     * @param _rewardsDistributor The address authorized to fund this reward
     * @param _rewardsDuration The duration over which rewards will be distributed
     * @dev Callable by OWNER_ROLE
     */
    function addReward(
        address _rewardsToken,
        address _rewardsDistributor,
        uint256 _rewardsDuration
    ) external requiresAuth {
        if (rewardData[_rewardsToken].rewardsDuration != 0) revert Distributor__AlreadyAdded();

        rewardTokens.push(_rewardsToken);
        rewardData[_rewardsToken].rewardsDistributor = _rewardsDistributor;
        rewardData[_rewardsToken].rewardsDuration = _rewardsDuration;
    }

    /**
     * @notice Update the rewards distributor for a reward token
     * @param _rewardsToken The reward token address
     * @param _rewardsDistributor The new distributor address
     * @dev Callable by OWNER_ROLE
     */
    function setRewardsDistributor(address _rewardsToken, address _rewardsDistributor) external requiresAuth {
        rewardData[_rewardsToken].rewardsDistributor = _rewardsDistributor;
    }

    /**
     * @notice Update the rewards duration for a reward token
     * @param _rewardsToken The reward token address
     * @param _rewardsDuration The new rewards duration
     * @dev Can only be called after the current reward period has finished
     * @dev Callable by the rewards distributor
     */
    function setRewardsDuration(address _rewardsToken, uint256 _rewardsDuration) external {
        if (block.timestamp <= rewardData[_rewardsToken].periodFinish) {
            revert Distributor__RewardPeriodActive();
        }
        if (rewardData[_rewardsToken].rewardsDistributor != msg.sender) revert Distributor__NotAuthorized();
        if (_rewardsDuration == 0) revert Distributor__ZeroDuration();

        rewardData[_rewardsToken].rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(_rewardsToken, rewardData[_rewardsToken].rewardsDuration);
    }

    /**
     * @notice Pause or unpause the contract
     * @param _paused Whether to pause the contract
     * @dev Callable by OWNER_ROLE
     */
    function setPaused(bool _paused) external requiresAuth {
        paused = _paused;
        emit PausedUpdated(_paused);
    }

    /**
     * @notice Recover ERC20 tokens sent to the contract by mistake
     * @param tokenAddress The token to recover
     * @param tokenAmount The amount to recover
     * @dev Cannot recover active reward tokens
     * @dev Callable by OWNER_ROLE
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external requiresAuth {
        if (rewardData[tokenAddress].lastUpdateTime != 0) revert Distributor__CannotWithdrawRewardToken();

        ERC20(tokenAddress).safeTransfer(msg.sender, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    // ========================================= REWARD DISTRIBUTION FUNCTIONS =========================================

    /**
     * @notice Notify the contract of a new reward amount
     * @param _rewardsToken The reward token address
     * @param reward The amount of rewards to distribute
     * @dev Transfers reward tokens from caller to this contract
     * @dev Callable by the rewards distributor for this token
     */
    function notifyRewardAmount(address _rewardsToken, uint256 reward) external updateReward(address(0)) {
        if (rewardData[_rewardsToken].rewardsDistributor != msg.sender) revert Distributor__NotAuthorized();

        // Transfer reward tokens from distributor to this contract
        ERC20(_rewardsToken).safeTransferFrom(msg.sender, address(this), reward);

        if (block.timestamp >= rewardData[_rewardsToken].periodFinish) {
            rewardData[_rewardsToken].rewardRate = reward / rewardData[_rewardsToken].rewardsDuration;
        } else {
            uint256 remaining = rewardData[_rewardsToken].periodFinish - block.timestamp;
            uint256 leftover = remaining * rewardData[_rewardsToken].rewardRate;
            rewardData[_rewardsToken].rewardRate = (reward + leftover) / rewardData[_rewardsToken].rewardsDuration;
        }

        rewardData[_rewardsToken].lastUpdateTime = block.timestamp;
        rewardData[_rewardsToken].periodFinish = block.timestamp + rewardData[_rewardsToken].rewardsDuration;

        emit RewardAdded(reward);
    }

    // ========================================= HOOK FUNCTIONS =========================================

    /**
     * @notice BeforeUpdate hook - updates rewards BEFORE balance changes
     * @param from The sender address (address(0) for mint)
     * @param to The recipient address (address(0) for burn)
     * @dev Called by vault BEFORE any balance change (mint/burn/transfer)
     * @dev Must update rewards based on OLD balances before they change
     * @dev Callable by vault only
     */
    function beforeUpdate(address from, address to, uint256 /* amount */, address /* operator */) external override {
        if (msg.sender != address(vault)) revert Distributor__NotAuthorized();
        if (paused) return; // Skip updates if paused

        // Update rewards for both parties BEFORE their balances change
        // address(0) means mint, skip sender update
        if (from != address(0)) {
            updateRewardForAccount(from);
        }
        // address(0) means burn, skip recipient update
        if (to != address(0)) {
            updateRewardForAccount(to);
        }
    }

    // ========================================= USER FUNCTIONS =========================================
    /**
     * @notice Claim accumulated rewards for specific tokens
     * @dev No staking required - rewards are based on vault share balance
     */
    function claimRewards(address[] memory _rewardTokens) public nonReentrant updateReward(msg.sender) {
        for (uint256 i = 0; i < _rewardTokens.length; i++) {
            address rwToken = _rewardTokens[i];
            uint256 reward = rewards[msg.sender][rwToken];

            if (reward > 0) {
                rewards[msg.sender][rwToken] = 0;
                ERC20(rwToken).safeTransfer(msg.sender, reward);
                emit RewardPaid(msg.sender, rwToken, reward);
            }
        }
    }

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Get the total supply of vault shares
     * @dev Reads directly from vault
     */
    function totalSupply() public view returns (uint256) {
        return vault.totalSupply();
    }

    /**
     * @notice Get the vault share balance of an account
     * @param account The account to query
     * @dev Reads directly from vault
     */
    function balanceOf(address account) public view returns (uint256) {
        return vault.balanceOf(account);
    }

    /**
     * @notice Get the last time rewards are applicable
     * @param _rewardsToken The reward token address
     */
    function lastTimeRewardApplicable(address _rewardsToken) public view returns (uint256) {
        return
            block.timestamp < rewardData[_rewardsToken].periodFinish
                ? block.timestamp
                : rewardData[_rewardsToken].periodFinish;
    }

    /**
     * @notice Calculate the current reward per token
     * @param _rewardsToken The reward token address
     */
    function rewardPerToken(address _rewardsToken) public view returns (uint256) {
        if (vault.totalSupply() == 0) {
            return rewardData[_rewardsToken].rewardPerTokenStored;
        }

        return
            rewardData[_rewardsToken].rewardPerTokenStored +
            ((lastTimeRewardApplicable(_rewardsToken) - rewardData[_rewardsToken].lastUpdateTime) *
                rewardData[_rewardsToken].rewardRate *
                1e18) /
                vault.totalSupply();
    }

    /**
     * @notice Calculate how much reward an account has earned
     * @param account The account to query
     * @param _rewardsToken The reward token address
     */
    function earned(address account, address _rewardsToken) public view returns (uint256) {
        return
            (vault.balanceOf(account) *
                (rewardPerToken(_rewardsToken) - userRewardPerTokenPaid[account][_rewardsToken])) /
                1e18 +
            rewards[account][_rewardsToken];
    }

    /**
     * @notice Get the total reward for the full duration
     * @param _rewardsToken The reward token address
     */
    function getRewardForDuration(address _rewardsToken) external view returns (uint256) {
        return rewardData[_rewardsToken].rewardRate * rewardData[_rewardsToken].rewardsDuration;
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Updates reward accounting for a specific account
     * @param account The account to update
     * @dev Can be called publicly to manually update rewards before balance queries
     */
    function updateRewardForAccount(address account) public {
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            address token = rewardTokens[i];
            rewardData[token].rewardPerTokenStored = rewardPerToken(token);
            rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);

            rewards[account][token] = earned(account, token);
            userRewardPerTokenPaid[account][token] = rewardData[token].rewardPerTokenStored;
        }
    }

    // ========================================= MODIFIERS =========================================

    /**
     * @notice Updates reward accounting for an account
     * @param account The account to update (address(0) updates global state only)
     */
    modifier updateReward(address account) {
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            address token = rewardTokens[i];
            rewardData[token].rewardPerTokenStored = rewardPerToken(token);
            rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);

            if (account != address(0)) {
                rewards[account][token] = earned(account, token);
                userRewardPerTokenPaid[account][token] = rewardData[token].rewardPerTokenStored;
            }
        }
        _;
    }

    /**
     * @notice Ensures the contract is not paused
     */
    modifier notPaused() {
        if (paused) revert Distributor__Paused();
        _;
    }
}

project/contracts/core/ManagerWithMerkle.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Auth, Authority} from "solmate/src/auth/Auth.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {MerkleProofLib} from "solmate/src/utils/MerkleProofLib.sol";
import {BoringVault} from "./BoringVault.sol";
import {IPausable} from "../interfaces/IPausable.sol";

import "../auth/PrimeAuth.sol";

/**
 * @title ManagerWithMerkleVerification
 * @author Prime Vaults
 * @notice Manages BoringVault operations with Merkle tree verification
 * @dev Each strategist can have their own Merkle root defining allowed operations
 *      Merkle tree leaves are: keccak256(abi.encodePacked(decoderAndSanitizer, target, valueIsNonZero, selector, argumentAddresses...))
 */
contract ManagerWithMerkleVerification is PrimeAuth, IPausable {
    using FixedPointMathLib for uint256;
    using Address for address;

    // ========================================= STATE =========================================

    /**
     * @notice The BoringVault this contract manages
     */
    BoringVault public immutable vault;

    /**
     * @notice Merkle root for each strategist defining their allowed operations
     * @dev Maps strategist address => merkle root
     */
    mapping(address => bytes32) public manageRoot;

    /**
     * @notice Pauses manageVaultWithMerkleVerification calls
     */
    bool public isPaused;

    // ========================================= EVENTS =========================================

    event ManageRootUpdated(address indexed strategist, bytes32 oldRoot, bytes32 newRoot);
    event BoringVaultManaged(uint256 callsMade);
    event Paused();
    event Unpaused();

    // ========================================= ERRORS =========================================

    error ManagerWithMerkleVerification__InvalidManageProofLength();
    error ManagerWithMerkleVerification__InvalidTargetDataLength();
    error ManagerWithMerkleVerification__InvalidValuesLength();
    error ManagerWithMerkleVerification__InvalidDecodersAndSanitizersLength();
    error ManagerWithMerkleVerification__FailedToVerifyManageProof(address target, bytes targetData, uint256 value);
    error ManagerWithMerkleVerification__Paused();
    error ManagerWithMerkleVerification__TotalSupplyMustRemainConstant();

    // ========================================= CONSTRUCTOR =========================================

    constructor(
        address _primeRBAC,
        address _vault
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = BoringVault(payable(_vault));
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Sets the Merkle root for a strategist
     * @param strategist Address of the strategist
     * @param _manageRoot The new Merkle root
     * @dev Callable by ADMIN_ROLE
     */
    function setManageRoot(address strategist, bytes32 _manageRoot) external onlyProtocolAdmin {
        bytes32 oldRoot = manageRoot[strategist];
        manageRoot[strategist] = _manageRoot;
        emit ManageRootUpdated(strategist, oldRoot, _manageRoot);
    }

    /**
     * @notice Pause manageVaultWithMerkleVerification calls
     * @dev Callable by EMERGENCY_ADMIN_ROLE
     */
    function pause() external onlyEmergencyAdmin {
        isPaused = true;
        emit Paused();
    }

    /**
     * @notice Unpause manageVaultWithMerkleVerification calls
     * @dev Callable by EMERGENCY_ADMIN_ROLE
     */
    function unpause() external onlyEmergencyAdmin {
        isPaused = false;
        emit Unpaused();
    }

    // ========================================= STRATEGIST FUNCTIONS =========================================

    /**
     * @notice Allows strategist to manage the BoringVault with Merkle verification
     * @param manageProofs Array of Merkle proofs for each operation
     * @param decodersAndSanitizers Array of decoder contract addresses
     * @param targets Array of target contract addresses
     * @param targetData Array of calldata for each target
     * @param values Array of ETH values for each call
     * @dev Callable by STRATEGIST_ROLE
     */
    function manageVaultWithMerkleVerification(
        bytes32[][] calldata manageProofs,
        address[] calldata decodersAndSanitizers,
        address[] calldata targets,
        bytes[] calldata targetData,
        uint256[] calldata values
    ) external requiresAuth {
        if (isPaused) revert ManagerWithMerkleVerification__Paused();

        uint256 targetsLength = targets.length;
        if (targetsLength != manageProofs.length) revert ManagerWithMerkleVerification__InvalidManageProofLength();
        if (targetsLength != targetData.length) revert ManagerWithMerkleVerification__InvalidTargetDataLength();
        if (targetsLength != values.length) revert ManagerWithMerkleVerification__InvalidValuesLength();
        if (targetsLength != decodersAndSanitizers.length) {
            revert ManagerWithMerkleVerification__InvalidDecodersAndSanitizersLength();
        }

        bytes32 strategistManageRoot = manageRoot[msg.sender];
        uint256 totalSupply = vault.totalSupply();

        for (uint256 i; i < targetsLength; ++i) {
            _verifyCallData(
                strategistManageRoot,
                manageProofs[i],
                decodersAndSanitizers[i],
                targets[i],
                values[i],
                targetData[i]
            );
            vault.manage(targets[i], targetData[i], values[i]);
        }

        // Ensure total supply hasn't changed (prevents minting/burning during management)
        if (totalSupply != vault.totalSupply()) {
            revert ManagerWithMerkleVerification__TotalSupplyMustRemainConstant();
        }

        emit BoringVaultManaged(targetsLength);
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Verifies a single manage call against the Merkle root
     * @param currentManageRoot The strategist's Merkle root
     * @param manageProof Merkle proof for this operation
     * @param decoderAndSanitizer Decoder contract address
     * @param target Target contract address
     * @param value ETH value to send
     * @param targetData Calldata for the target
     */
    function _verifyCallData(
        bytes32 currentManageRoot,
        bytes32[] calldata manageProof,
        address decoderAndSanitizer,
        address target,
        uint256 value,
        bytes calldata targetData
    ) internal view {
        // Extract function selector
        bytes4 selector = bytes4(targetData);

        // Get packed argument addresses from decoder
        bytes memory packedArgumentAddresses = abi.decode(decoderAndSanitizer.functionStaticCall(targetData), (bytes));

        if (
            !_verifyManageProof(
                currentManageRoot,
                manageProof,
                target,
                decoderAndSanitizer,
                value,
                selector,
                packedArgumentAddresses
            )
        ) {
            revert ManagerWithMerkleVerification__FailedToVerifyManageProof(target, targetData, value);
        }
    }

    /**
     * @notice Verifies a Merkle proof
     * @param root Merkle root
     * @param proof Merkle proof
     * @param target Target contract address
     * @param decoderAndSanitizer Decoder contract address
     * @param value ETH value
     * @param selector Function selector
     * @param packedArgumentAddresses Packed address arguments
     * @return bool True if proof is valid
     */
    function _verifyManageProof(
        bytes32 root,
        bytes32[] calldata proof,
        address target,
        address decoderAndSanitizer,
        uint256 value,
        bytes4 selector,
        bytes memory packedArgumentAddresses
    ) internal pure returns (bool) {
        bool valueNonZero = value > 0;
        bytes32 leaf = keccak256(
            abi.encodePacked(decoderAndSanitizer, target, valueNonZero, selector, packedArgumentAddresses)
        );
        return MerkleProofLib.verify(proof, root, leaf);
    }
}

