// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {PrimeAuth} from "../auth/PrimeAuth.sol";
import {RolesAuthority} from "solmate/src/auth/authorities/RolesAuthority.sol";
import {Authority} from "solmate/src/auth/Auth.sol";
import {BoringVault} from "./BoringVault.sol";
import {AccountantWithYieldStreaming} from "./AccountantWithYieldStreaming.sol";
import {TellerWithYieldStreaming} from "./TellerWithYieldStreaming.sol";
import {ManagerWithMerkleVerification} from "./ManagerWithMerkleVerification.sol";
import {DelayedWithdraw} from "./DelayedWithdraw.sol";
import {ERC20} from "solmate/src/tokens/ERC20.sol";

/**
 * @title PrimeRegistry
 * @author PrimeVaults Labs
 * @notice Registry contract for tracking and deploying Prime Vault instances
 * @dev Factory contract that creates new vaults with standardized naming
 */
contract PrimeRegistry is PrimeAuth {
    //============================== STRUCTS ===============================

    struct VaultComponents {
        address vault;
        address accountant;
        address teller;
        address manager;
        address withdrawer;
    }

    //============================== CONSTANTS ===============================
    uint8 public constant ADMIN_ROLE = 1;
    uint8 public constant MANAGER_ROLE = 2;
    uint8 public constant MINTER_ROLE = 3;
    uint8 public constant BORING_VAULT_ROLE = 4;
    uint8 public constant STRATEGIST_ROLE = 7;
    uint8 public constant BURNER_ROLE = 8;
    uint8 public constant SOLVER_ROLE = 9;
    uint8 public constant QUEUE_ROLE = 10;
    uint8 public constant CAN_SOLVE_ROLE = 11;

    //============================== STATE ===============================

    /**
     * @notice Mapping of vault address to its components
     */
    mapping(address => VaultComponents) public vaults;

    /**
     * @notice Array of all registered vault addresses
     */
    address[] public vaultList;

    //============================== EVENTS ===============================

    event VaultCreated(address indexed vault, address indexed asset, address indexed rolesAuthority);
    event AccountantRegistered(address indexed accountant, address indexed vault);
    event TellerRegistered(address indexed teller, address indexed vault);
    event ManagerRegistered(address indexed manager, address indexed vault);
    event WithdrawerRegistered(address indexed withdrawer, address indexed vault);

    //============================== ERRORS ===============================

    error PrimeRegistry__ZeroAddress();
    error PrimeRegistry__InvalidAuthority();
    error PrimeRegistry__AccountantNotRegistered();
    error PrimeRegistry__TellerNotRegistered();
    error PrimeRegistry__WithdrawerNotRegistered();

    //============================== CONSTRUCTOR ===============================

    constructor(address _primeRBAC) PrimeAuth(_primeRBAC, address(0)) {}

    //============================== ADMIN FUNCTIONS ===============================

    /**
     * @notice Register and setup permissions for a vault
     * @param vault The BoringVault to register
     */
    function registerVault(BoringVault vault) public onlyProtocolAdmin {
        Authority authority = vault.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));

        // Set role capabilities for vault management
        rolesAuthority.setRoleCapability(MANAGER_ROLE, address(vault), BoringVault.manage.selector, true);
        rolesAuthority.setRoleCapability(MANAGER_ROLE, address(vault), BoringVault.bulkManage.selector, true);

        // Set role capabilities for minting and burning shares
        rolesAuthority.setRoleCapability(MINTER_ROLE, address(vault), BoringVault.enter.selector, true);
        rolesAuthority.setRoleCapability(BURNER_ROLE, address(vault), BoringVault.exit.selector, true);

        rolesAuthority.setUserRole(address(vault), BORING_VAULT_ROLE, true);

        vaultList.push(address(vault));
        vaults[address(vault)].vault = address(vault);
        emit VaultCreated(address(vault), address(vault.asset()), address(rolesAuthority));
    }

    /**
     * @notice Register and setup permissions for an accountant
     * @param accountant The AccountantWithYieldStreaming to register
     */
    function registerAccountant(AccountantWithYieldStreaming accountant) public onlyProtocolAdmin {
        Authority authority = accountant.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));

        // Set role capabilities for Accountant functions
        rolesAuthority.setRoleCapability(
            MINTER_ROLE,
            address(accountant),
            accountant.setFirstDepositTimestamp.selector,
            true
        );
        rolesAuthority.setRoleCapability(
            STRATEGIST_ROLE,
            address(accountant),
            bytes4(keccak256("updateExchangeRate()")),
            true
        );

        rolesAuthority.setUserRole(address(accountant), MINTER_ROLE, true);
        rolesAuthority.setUserRole(address(accountant), ADMIN_ROLE, true);
        rolesAuthority.setUserRole(address(accountant), STRATEGIST_ROLE, true);

        vaults[address(accountant.vault())].accountant = address(accountant);
        emit AccountantRegistered(address(accountant), address(accountant.vault()));
    }

    /**
     * @notice Register and setup permissions for a teller
     * @param teller The TellerWithYieldStreaming to register
     */
    function registerTeller(TellerWithYieldStreaming teller) public onlyProtocolAdmin {
        Authority authority = teller.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));

        // Set public capability for deposit
        rolesAuthority.setPublicCapability(address(teller), teller.deposit.selector, true);

        // Set role capabilities for Teller functions
        rolesAuthority.setRoleCapability(MINTER_ROLE, address(teller), teller.depositWithPermit.selector, true);
        rolesAuthority.setRoleCapability(BURNER_ROLE, address(teller), teller.withdraw.selector, true);
        rolesAuthority.setRoleCapability(BURNER_ROLE, address(teller), teller.bulkWithdraw.selector, true);

        // Set role capabilities for buffer helper management
        rolesAuthority.setRoleCapability(MANAGER_ROLE, address(teller), teller.setDepositBufferHelper.selector, true);
        rolesAuthority.setRoleCapability(MANAGER_ROLE, address(teller), teller.setWithdrawBufferHelper.selector, true);
        rolesAuthority.setRoleCapability(MANAGER_ROLE, address(teller), teller.allowBufferHelper.selector, true);

        rolesAuthority.setUserRole(address(teller), MINTER_ROLE, true);
        rolesAuthority.setUserRole(address(teller), BURNER_ROLE, true);
        rolesAuthority.setUserRole(address(teller), MANAGER_ROLE, true);
        rolesAuthority.setUserRole(address(teller), STRATEGIST_ROLE, true);

        vaults[address(teller.vault())].teller = address(teller);
        emit TellerRegistered(address(teller), address(teller.vault()));
    }

    /**
     * @notice Register and setup permissions for a manager
     * @param manager The ManagerWithMerkleVerification to register
     */
    function registerManager(ManagerWithMerkleVerification manager) public onlyProtocolAdmin {
        Authority authority = manager.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));

        // Set role capability for manageVaultWithMerkleVerification
        rolesAuthority.setRoleCapability(
            STRATEGIST_ROLE,
            address(manager),
            bytes4(keccak256("manageVaultWithMerkleVerification(bytes32[][],address[],address[],bytes[],uint256[]")),
            true
        );

        // Grant MANAGER_ROLE to manager contract (so it can call vault.manage())
        rolesAuthority.setUserRole(address(manager), MANAGER_ROLE, true);

        vaults[address(manager.vault())].manager = address(manager);
        emit ManagerRegistered(address(manager), address(manager.vault()));
    }

    /**
     * @notice Grant STRATEGIST_ROLE to an address (e.g., admin for testing)
     * @param strategist The address to grant the role to
     * @param vault The vault whose RolesAuthority to update
     */
    function grantStrategistRole(address strategist, BoringVault vault) public onlyProtocolAdmin {
        Authority authority = vault.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));
        rolesAuthority.setUserRole(strategist, STRATEGIST_ROLE, true);
    }

    /**
     * @notice Register and setup permissions for a withdrawer
     * @param withdrawer The DelayedWithdraw to register
     */
    function registerWithdrawer(DelayedWithdraw withdrawer) public onlyProtocolAdmin {
        Authority authority = withdrawer.authority();
        RolesAuthority rolesAuthority = RolesAuthority(address(authority));

        // Set public capabilities for withdrawer user functions
        rolesAuthority.setPublicCapability(address(withdrawer), withdrawer.cancelWithdraw.selector, true);
        rolesAuthority.setPublicCapability(address(withdrawer), withdrawer.requestWithdraw.selector, true);
        rolesAuthority.setPublicCapability(address(withdrawer), withdrawer.completeWithdraw.selector, true);
        rolesAuthority.setPublicCapability(address(withdrawer), withdrawer.setAllowThirdPartyToComplete.selector, true);

        rolesAuthority.setUserRole(address(withdrawer), BURNER_ROLE, true);

        vaults[address(withdrawer.boringVault())].withdrawer = address(withdrawer);
        emit WithdrawerRegistered(address(withdrawer), address(withdrawer.boringVault()));
    }

    //============================== VIEW FUNCTIONS ===============================

    /**
     * @notice Get list of all registered vaults with their components
     * @return components Array of VaultComponents structs
     */
    function getVaults() external view returns (VaultComponents[] memory components) {
        uint256 total = vaultList.length;
        components = new VaultComponents[](total);
        for (uint256 i = 0; i < total; i++) {
            components[i] = vaults[vaultList[i]];
        }
        return components;
    }
}

project/contracts/core/TellerWithBuffer..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {TellerWithMultiAssetSupport, ERC20} from "./TellerWithMultiAssetSupport.sol";
import {IBufferHelper} from "../interfaces/IBufferHelper.sol";

/**
 * @title TellerWithBuffer
 * @author PrimeVaults Labs
 * @notice A teller contract that integrates with buffer helpers to manage deposits and withdrawals
 * @dev Extends TellerWithMultiAssetSupport to add automatic yield and withdrawal buffer capabilities.
 * The buffer helpers can trigger additional vault management calls during these operations.
 */
contract TellerWithBuffer is TellerWithMultiAssetSupport {
    //============================== STRUCTS ===============================
    struct BufferHelpers {
        IBufferHelper depositBufferHelper;
        IBufferHelper withdrawBufferHelper;
    }

    //============================== STATE ===============================
    BufferHelpers public bufferHelpers;

    mapping(IBufferHelper => bool) public allowedBufferHelpers;

    //============================== ERRORS ===============================
    error TellerWithBuffer__BufferHelperNotAllowed(IBufferHelper bufferHelper);

    //============================== EVENTS ===============================
    event DepositBufferHelperSet(IBufferHelper indexed newDepositBufferHelper);
    event WithdrawBufferHelperSet(IBufferHelper indexed newWithdrawBufferHelper);
    event BufferHelperAllowed(IBufferHelper indexed bufferHelper);
    event BufferHelperDisallowed(IBufferHelper indexed bufferHelper);

    /**
     * @notice Initializes the TellerWithBuffer contract
     * @param _primeRBAC The PrimeRBAC contract address for authentication
     * @param _vault The vault contract address this teller will interact with
     * @param _accountant The accountant contract address associated with the vault
     */
    constructor(
        address _primeRBAC,
        address _vault,
        address _accountant
    ) TellerWithMultiAssetSupport(_primeRBAC, _vault, _accountant) {}

    /**
     * @notice Executes buffer management after a deposit operation
     * @param assetAmount The amount of the asset deposited
     * @dev This function is called internally after a deposit is processed.
     * If a deposit buffer helper is configured, it will retrieve management calls
     * and execute them through the vault's manage function.
     */
    function _afterDeposit(uint256 assetAmount) internal override {
        if (address(bufferHelpers.depositBufferHelper) != address(0)) {
            (address[] memory targets, bytes[] memory data, uint256[] memory values) = bufferHelpers
                .depositBufferHelper
                .getDepositManageCall(address(asset), assetAmount);
            vault.bulkManage(targets, data, values);
        }
    }

    /**
     * @notice Executes buffer management before a withdrawal operation
     * @param assetAmount The amount of the asset being withdrawn
     * @dev This function is called internally before a withdrawal is processed.
     * If a withdraw buffer helper is configured, it will retrieve management calls
     * and execute them through the vault's manage function.
     */
    function _beforeWithdraw(uint256 assetAmount) internal override {
        if (address(bufferHelpers.withdrawBufferHelper) != address(0)) {
            (address[] memory targets, bytes[] memory data, uint256[] memory values) = bufferHelpers
                .withdrawBufferHelper
                .getWithdrawManageCall(address(asset), assetAmount);
            vault.bulkManage(targets, data, values);
        }
    }

    /**
     * @notice Updates the deposit buffer helper contract
     * @param _depositBufferHelper The new deposit buffer helper contract address
     * @dev Only callable by authorized accounts. This allows for dynamic updates
     * to the deposit management strategy without requiring contract redeployment.
     */
    function setDepositBufferHelper(IBufferHelper _depositBufferHelper) external onlyProtocolAdmin {
        if (allowedBufferHelpers[_depositBufferHelper] || _depositBufferHelper == IBufferHelper(address(0))) {
            bufferHelpers.depositBufferHelper = _depositBufferHelper;
            emit DepositBufferHelperSet(_depositBufferHelper);
        } else {
            revert TellerWithBuffer__BufferHelperNotAllowed(_depositBufferHelper);
        }
    }

    /**
     * @notice Updates the withdrawal buffer helper contract
     * @param _withdrawBufferHelper The new withdrawal buffer helper contract address
     * @dev Only callable by authorized accounts. This allows for dynamic updates
     * to the withdrawal management strategy without requiring contract redeployment.
     */
    function setWithdrawBufferHelper(IBufferHelper _withdrawBufferHelper) external onlyProtocolAdmin {
        if (allowedBufferHelpers[_withdrawBufferHelper] || _withdrawBufferHelper == IBufferHelper(address(0))) {
            bufferHelpers.withdrawBufferHelper = _withdrawBufferHelper;
            emit WithdrawBufferHelperSet(_withdrawBufferHelper);
        } else {
            revert TellerWithBuffer__BufferHelperNotAllowed(_withdrawBufferHelper);
        }
    }

    /**
     * @notice Allows a buffer helper to be used
     * @param _bufferHelper The buffer helper contract address to allow
     * @dev Only callable by admin to allowlist buffer helpers
     */
    function allowBufferHelper(IBufferHelper _bufferHelper) external onlyProtocolAdmin {
        allowedBufferHelpers[_bufferHelper] = true;
        emit BufferHelperAllowed(_bufferHelper);
    }

    /**
     * @notice Disallows a buffer helper from being used
     * @param _bufferHelper The buffer helper contract address to disallow
     * @dev Only callable by admin to disallow buffer helpers
     */
    function disallowBufferHelper(IBufferHelper _bufferHelper) external requiresAuth {
        allowedBufferHelpers[_bufferHelper] = false;
        emit BufferHelperDisallowed(_bufferHelper);
    }

    /**
     * @notice Returns the version of the contract.
     */
    function version() public pure virtual override returns (string memory) {
        return string(abi.encodePacked("Buffer V0.1, ", super.version()));
    }
}

project/contracts/core/TellerWithMultiAs.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ERC20} from "solmate/src/tokens/ERC20.sol";
import {BoringVault} from "./BoringVault.sol";
import {AccountantWithRateProviders} from "./AccountantWithRateProviders.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {IBeforeUpdateHook} from "../interfaces/hooks/IBeforeUpdateHook.sol";
import {ReentrancyGuard} from "solmate/src/utils/ReentrancyGuard.sol";
import {IPausable} from "../interfaces/IPausable.sol";
import {Distributor} from "./Distributor.sol";

import "../auth/PrimeAuth.sol";

contract TellerWithMultiAssetSupport is PrimeAuth, IBeforeUpdateHook, ReentrancyGuard, IPausable {
    using FixedPointMathLib for uint256;
    using SafeTransferLib for ERC20;

    // ========================================= STRUCTS =========================================

    /**
     * @param isPaused whether or not this contract is paused
     * @param allowDeposits whether deposits are allowed for the asset
     * @param allowWithdraws whether withdrawals are allowed for the asset
     * @param permissionedTransfers if true, only permissioned operators can transfer shares
     * @param shareLockPeriod after deposits, shares are locked to the msg.sender's address for this period
     * @param depositCap the global deposit cap of the vault
     */
    struct TellerState {
        bool isPaused;
        bool allowDeposits;
        bool allowWithdraws;
        bool permissionedTransfers;
        uint64 shareLockPeriod;
        uint112 depositCap;
    }

    /**
     * @param denyFrom bool indicating whether or not the user is on the deny from list.
     * @param denyTo bool indicating whether or not the user is on the deny to list.
     * @param denyOperator bool indicating whether or not the user is on the deny operator list.
     * @param permissionedOperator bool indicating whether or not the user is a permissioned operator, only applies when permissionedTransfers is true.
     * @param shareUnlockTime uint256 indicating the time at which the shares will be unlocked.
     */
    struct BeforeTransferData {
        bool denyFrom;
        bool denyTo;
        bool denyOperator;
        bool permissionedOperator;
        uint256 shareUnlockTime;
    }

    // ========================================= CONSTANTS =========================================

    /**
     * @notice The maximum possible share lock period.
     */
    uint256 internal constant MAX_SHARE_LOCK_PERIOD = 3 days;

    // ========================================= STATE =========================================

    /**
     * @notice The single asset this teller supports
     */
    ERC20 public immutable asset;

    /**
     * @notice Store the teller state in a packed slot.
     */
    TellerState public tellerState;

    /**
     * @notice Maps address to BeforeTransferData struct to check if shares are locked and if the address is on any allow or deny list.
     */
    mapping(address => BeforeTransferData) public beforeTransferData;

    //============================== ERRORS ===============================

    error TellerWithMultiAssetSupport__ShareLockPeriodTooLong();
    error TellerWithMultiAssetSupport__SharesAreLocked();
    error TellerWithMultiAssetSupport__ZeroAssets();
    error TellerWithMultiAssetSupport__MinimumMintNotMet();
    error TellerWithMultiAssetSupport__MinimumAssetsNotMet();
    error TellerWithMultiAssetSupport__PermitFailedAndAllowanceTooLow();
    error TellerWithMultiAssetSupport__ZeroShares();
    error TellerWithMultiAssetSupport__DualDeposit();
    error TellerWithMultiAssetSupport__Paused();
    error TellerWithMultiAssetSupport__TransferDenied(address from, address to, address operator);
    error TellerWithMultiAssetSupport__DepositExceedsCap();
    error TellerWithMultiAssetSupport__DepositsNotAllowed();
    error TellerWithMultiAssetSupport__WithdrawsNotAllowed();

    //============================== EVENTS ===============================

    event Paused();
    event Unpaused();
    event DepositsAllowed(bool allowed);
    event WithdrawsAllowed(bool allowed);
    event Deposit(
        uint256 nonce,
        address indexed receiver,
        uint256 depositAmount,
        uint256 shareAmount,
        uint256 depositTimestamp,
        uint256 shareLockPeriodAtTimeOfDeposit,
        address indexed referralAddress
    );
    event BulkDeposit(address indexed asset, uint256 depositAmount);
    event BulkWithdraw(address indexed asset, uint256 shareAmount);
    event Withdraw(address indexed asset, uint256 shareAmount);
    event DenyFrom(address indexed user);
    event DenyTo(address indexed user);
    event DenyOperator(address indexed user);
    event AllowFrom(address indexed user);
    event AllowTo(address indexed user);
    event AllowOperator(address indexed user);
    event PermissionedTransfersSet(bool permissionedTransfers);
    event AllowPermissionedOperator(address indexed operator);
    event DenyPermissionedOperator(address indexed operator);
    event DepositCapSet(uint112 cap);

    //============================== IMMUTABLES ===============================

    /**
     * @notice The BoringVault this contract is working with.
     */
    BoringVault public immutable vault;

    /**
     * @notice The AccountantWithRateProviders this contract is working with.
     */
    AccountantWithRateProviders public immutable accountant;

    /**
     * @notice The Distributor contract for reward distribution (optional).
     */
    Distributor public distributor;

    /**
     * @notice One share of the BoringVault.
     */
    uint256 internal immutable ONE_SHARE;

    constructor(
        address _primeRBAC,
        address _vault,
        address _accountant
    ) PrimeAuth(_primeRBAC, address(BoringVault(payable(_vault)).authority())) {
        vault = BoringVault(payable(_vault));
        ONE_SHARE = 10 ** vault.decimals();
        accountant = AccountantWithRateProviders(_accountant);
        asset = vault.asset();
        tellerState = TellerState({
            isPaused: false,
            allowDeposits: true,
            allowWithdraws: true,
            permissionedTransfers: false,
            shareLockPeriod: 0,
            depositCap: type(uint112).max
        });
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Pause this contract, which prevents future calls to `deposit` and `depositWithPermit`.
     * @dev Callable by MULTISIG_ROLE.
     */
    function pause() external requiresAuth {
        tellerState.isPaused = true;
        emit Paused();
    }

    /**
     * @notice Unpause this contract, which allows future calls to `deposit` and `depositWithPermit`.
     * @dev Callable by MULTISIG_ROLE.
     */
    function unpause() external requiresAuth {
        tellerState.isPaused = false;
        emit Unpaused();
    }

    /**
     * @notice Enable or disable deposits for the asset.
     * @dev Callable by OWNER_ROLE.
     */
    function setAllowDeposits(bool _allowDeposits) external onlyProtocolAdmin {
        tellerState.allowDeposits = _allowDeposits;
        emit DepositsAllowed(_allowDeposits);
    }

    /**
     * @notice Enable or disable withdrawals for the asset.
     * @dev Callable by OWNER_ROLE.
     */
    function setAllowWithdraws(bool _allowWithdraws) external onlyProtocolAdmin {
        tellerState.allowWithdraws = _allowWithdraws;
        emit WithdrawsAllowed(_allowWithdraws);
    }

    /**
     * @notice Set the Distributor contract for reward distribution.
     * @dev Callable by OWNER_ROLE.
     */
    function setDistributor(address _distributor) external onlyProtocolAdmin {
        distributor = Distributor(_distributor);
    }

    /**
     * @notice Sets the share lock period.
     * @dev This locks shares to the user address for the specified period.
     * @dev Callable by OWNER_ROLE.
     */
    function setShareLockPeriod(uint64 _shareLockPeriod) external requiresAuth {
        if (_shareLockPeriod > MAX_SHARE_LOCK_PERIOD) revert TellerWithMultiAssetSupport__ShareLockPeriodTooLong();
        tellerState.shareLockPeriod = _shareLockPeriod;
    }

    /**
     * @notice Deny a user from transferring or receiving shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function denyAll(address user) external requiresAuth {
        beforeTransferData[user].denyFrom = true;
        beforeTransferData[user].denyTo = true;
        beforeTransferData[user].denyOperator = true;
        emit DenyFrom(user);
        emit DenyTo(user);
        emit DenyOperator(user);
    }

    /**
     * @notice Allow a user to transfer or receive shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function allowAll(address user) external requiresAuth {
        beforeTransferData[user].denyFrom = false;
        beforeTransferData[user].denyTo = false;
        beforeTransferData[user].denyOperator = false;
        emit AllowFrom(user);
        emit AllowTo(user);
        emit AllowOperator(user);
    }

    /**
     * @notice Deny a user from transferring shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function denyFrom(address user) external requiresAuth {
        beforeTransferData[user].denyFrom = true;
        emit DenyFrom(user);
    }

    /**
     * @notice Allow a user to transfer shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function allowFrom(address user) external requiresAuth {
        beforeTransferData[user].denyFrom = false;
        emit AllowFrom(user);
    }

    /**
     * @notice Deny a user from receiving shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function denyTo(address user) external requiresAuth {
        beforeTransferData[user].denyTo = true;
        emit DenyTo(user);
    }

    /**
     * @notice Allow a user to receive shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function allowTo(address user) external requiresAuth {
        beforeTransferData[user].denyTo = false;
        emit AllowTo(user);
    }

    /**
     * @notice Deny an operator from transferring shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function denyOperator(address user) external requiresAuth {
        beforeTransferData[user].denyOperator = true;
        emit DenyOperator(user);
    }

    /**
     * @notice Allow an operator to transfer shares.
     * @dev Callable by OWNER_ROLE, and DENIER_ROLE.
     */
    function allowOperator(address user) external requiresAuth {
        beforeTransferData[user].denyOperator = false;
        emit AllowOperator(user);
    }

    /**
     * @notice Set the permissioned transfers flag.
     * @dev Callable by OWNER_ROLE.
     */
    function setPermissionedTransfers(bool _permissionedTransfers) external requiresAuth {
        tellerState.permissionedTransfers = _permissionedTransfers;
        emit PermissionedTransfersSet(_permissionedTransfers);
    }

    /**
     * @notice Give permission to an operator to transfer shares when permissioned transfers flag is true.
     * @dev Callable by OWNER_ROLE.
     */
    function allowPermissionedOperator(address operator) external requiresAuth {
        beforeTransferData[operator].permissionedOperator = true;
        emit AllowPermissionedOperator(operator);
    }

    /**
     * @notice Revoke permission from an operator to transfer shares when permissioned transfers flag is true.
     * @dev Callable by OWNER_ROLE, and DENIER_.
     */
    function denyPermissionedOperator(address operator) external requiresAuth {
        beforeTransferData[operator].permissionedOperator = false;
        emit DenyPermissionedOperator(operator);
    }

    /**
     * @notice Set the deposit cap of the vault.
     * @dev Callable by OWNER_ROLE
     */
    function setDepositCap(uint112 cap) external requiresAuth {
        tellerState.depositCap = cap;
        emit DepositCapSet(cap);
    }

    // ========================================= IBeforeUpdateHook FUNCTIONS =========================================

    /**
     * @notice Implement beforeUpdate hook to check if shares are locked, or if `from`, `to`, or `operator` are denied.
     * @notice If permissionedTransfers is true, then only operators on the allow list can transfer shares.
     * @notice If share lock period is set to zero, then users will be able to mint and transfer in the same tx.
     *         if this behavior is not desired then a share lock period of >=1 should be used.
     * @notice When minting (from = address(0)), only checks `to` and `operator`.
     * @notice When burning (to = address(0)), only checks `from` and `operator`.
     */
    function beforeUpdate(address from, address to, uint256 /* amount */, address operator) public virtual {
        // Update rewards BEFORE balance changes if distributor is set
        if (address(distributor) != address(0)) {
            if (from != address(0)) {
                distributor.updateRewardForAccount(from);
            }
            if (to != address(0)) {
                distributor.updateRewardForAccount(to);
            }
        }

        // For minting: from = address(0), skip from checks
        if (from != address(0)) {
            // Check if from is denied
            if (beforeTransferData[from].denyFrom) {
                revert TellerWithMultiAssetSupport__TransferDenied(from, to, operator);
            }
            // Check if shares are locked
            if (beforeTransferData[from].shareUnlockTime > block.timestamp) {
                revert TellerWithMultiAssetSupport__SharesAreLocked();
            }
        }

        // For burning: to = address(0), skip to checks
        if (to != address(0)) {
            // Check if to is denied
            if (beforeTransferData[to].denyTo) {
                revert TellerWithMultiAssetSupport__TransferDenied(from, to, operator);
            }
        }

        // Always check operator (unless it's address(0))
        if (operator != address(0) && beforeTransferData[operator].denyOperator) {
            revert TellerWithMultiAssetSupport__TransferDenied(from, to, operator);
        }

        // Check permissioned transfers (only for transfers, not mint/burn)
        if (from != address(0) && to != address(0)) {
            if (tellerState.permissionedTransfers && !beforeTransferData[operator].permissionedOperator) {
                revert TellerWithMultiAssetSupport__TransferDenied(from, to, operator);
            }
        }
    }

    /**
     * @notice Internal function to check deny lists for transfers.
     * @dev Reverts if `from` is denied, `to` is denied, or `operator` is denied.
     * @param from The sender address.
     * @param to The receiver address.
     * @param operator The address performing the operation.
     */
    function _handleDenyList(address from, address to, address operator) internal view {
        if (
            beforeTransferData[from].denyFrom ||
            beforeTransferData[to].denyTo ||
            beforeTransferData[operator].denyOperator
        ) {
            revert TellerWithMultiAssetSupport__TransferDenied(from, to, operator);
        }
    }

    // ========================================= USER FUNCTIONS =========================================

    /**
     * @notice Allows users to deposit into the BoringVault, if this contract is not paused.
     * @dev Publicly callable.
     */
    function deposit(
        uint256 depositAmount,
        uint256 minimumMint,
        address referralAddress
    ) external virtual requiresAuth nonReentrant returns (uint256 shares) {
        _beforeDeposit();
        shares = _erc20Deposit(depositAmount, minimumMint, msg.sender, msg.sender);
        _afterPublicDeposit(msg.sender, depositAmount, shares, tellerState.shareLockPeriod, referralAddress);
    }

    /**
     * @notice Allows users to deposit into BoringVault using permit.
     * @dev Publicly callable.
     */
    function depositWithPermit(
        uint256 depositAmount,
        uint256 minimumMint,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s,
        address referralAddress
    ) external virtual requiresAuth nonReentrant returns (uint256 shares) {
        _beforeDeposit();
        _handlePermit(depositAmount, deadline, v, r, s);
        shares = _erc20Deposit(depositAmount, minimumMint, msg.sender, msg.sender);
        _afterPublicDeposit(msg.sender, depositAmount, shares, tellerState.shareLockPeriod, referralAddress);
    }

    /**
     * @notice Allows on ramp role to deposit into this contract.
     * @dev Does NOT support native deposits.
     * @dev Callable by SOLVER_ROLE.
     */
    function bulkDeposit(
        uint256 depositAmount,
        uint256 minimumMint,
        address to
    ) external virtual requiresAuth nonReentrant returns (uint256 shares) {
        _beforeDeposit();
        shares = _erc20Deposit(depositAmount, minimumMint, msg.sender, to);
        emit BulkDeposit(address(asset), depositAmount);
    }

    /**
     * @notice Allows off ramp role to withdraw from this contract.
     * @dev Callable by SOLVER_ROLE.
     */
    function bulkWithdraw(
        uint256 shareAmount,
        uint256 minimumAssets,
        address to
    ) external virtual requiresAuth nonReentrant returns (uint256 assetsOut) {
        assetsOut = _withdraw(shareAmount, minimumAssets, to);
        emit BulkWithdraw(address(asset), shareAmount);
    }

    /**
     * @notice Allows withdrawals from this contract.
     * @dev Either public or disabled depending on configuration.
     */
    function withdraw(
        uint256 shareAmount,
        uint256 minimumAssets,
        address to
    ) external virtual requiresAuth nonReentrant returns (uint256 assetsOut) {
        assetsOut = _withdraw(shareAmount, minimumAssets, to);
        emit Withdraw(address(asset), shareAmount);
    }

    // ========================================= INTERNAL HELPER FUNCTIONS =========================================

    /**
     * @notice Implements a common ERC20 deposit into BoringVault.
     */
    function _erc20Deposit(
        uint256 depositAmount,
        uint256 minimumMint,
        address from,
        address to
    ) internal virtual returns (uint256 shares) {
        _handleDenyList(from, to, msg.sender);
        TellerState storage state = tellerState;
        if (depositAmount == 0) revert TellerWithMultiAssetSupport__ZeroAssets();
        shares = depositAmount.mulDivDown(ONE_SHARE, accountant.getRate());
        if (shares < minimumMint) revert TellerWithMultiAssetSupport__MinimumMintNotMet();
        if (state.depositCap != type(uint112).max) {
            if (shares + vault.totalSupply() > state.depositCap)
                revert TellerWithMultiAssetSupport__DepositExceedsCap();
        }
        vault.enter(from, depositAmount, to, shares);
        _afterDeposit(depositAmount);
    }

    /**
     * @notice Implements a common ERC20 withdraw from BoringVault.
     */
    function _withdraw(
        uint256 shareAmount,
        uint256 minimumAssets,
        address to
    ) internal virtual returns (uint256 assetsOut) {
        TellerState storage state = tellerState;
        if (state.isPaused) revert TellerWithMultiAssetSupport__Paused();
        if (!state.allowWithdraws) revert TellerWithMultiAssetSupport__WithdrawsNotAllowed();

        if (shareAmount == 0) revert TellerWithMultiAssetSupport__ZeroShares();
        assetsOut = shareAmount.mulDivDown(accountant.getRate(), ONE_SHARE);
        if (assetsOut < minimumAssets) revert TellerWithMultiAssetSupport__MinimumAssetsNotMet();
        _beforeWithdraw(assetsOut);
        vault.exit(to, assetsOut, msg.sender, shareAmount);
    }

    /**
     * @notice Handle pre-deposit checks.
     */
    function _beforeDeposit() internal view {
        TellerState storage state = tellerState;
        if (state.isPaused) revert TellerWithMultiAssetSupport__Paused();
        if (!state.allowDeposits) revert TellerWithMultiAssetSupport__DepositsNotAllowed();
    }

    /**
     * @notice Handle share lock logic, and event.
     */
    function _afterPublicDeposit(
        address user,
        uint256 depositAmount,
        uint256 shares,
        uint256 currentShareLockPeriod,
        address referralAddress
    ) internal {
        // Only set share unlock time if share lock period is greater than 0.
        if (currentShareLockPeriod > 0) {
            beforeTransferData[user].shareUnlockTime = block.timestamp + currentShareLockPeriod;
        }
        emit Deposit(
            0, // nonce no longer used
            user,
            depositAmount,
            shares,
            block.timestamp,
            currentShareLockPeriod,
            referralAddress
        );
    }

    /**
     * @notice Handle permit logic.
     */
    function _handlePermit(uint256 depositAmount, uint256 deadline, uint8 v, bytes32 r, bytes32 s) internal {
        try asset.permit(msg.sender, address(vault), depositAmount, deadline, v, r, s) {} catch {
            if (asset.allowance(msg.sender, address(vault)) < depositAmount) {
                revert TellerWithMultiAssetSupport__PermitFailedAndAllowanceTooLow();
            }
        }
    }

    /**
     * @notice Hook that is called after a deposit operation.
     * @dev Can be overridden by child contracts to implement custom post-deposit logic.
     * @param assetAmount The amount of the asset that was deposited.
     */
    function _afterDeposit(uint256 assetAmount) internal virtual {}

    /**
     * @notice Hook that is called before a withdrawal operation.
     * @dev Can be overridden by child contracts to implement custom pre-withdrawal logic.
     * @param assetAmount The amount of the asset that will be withdrawn.
     */
    function _beforeWithdraw(uint256 assetAmount) internal virtual {}

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Returns the version of the contract.
     */
    function version() public pure virtual returns (string memory) {
        return "Base V0.1";
    }
}

