library Error {
    //////////////////////////////////////////////////////////////
    //                  INPUT VALIDATION ERRORS                 //
    /////////////////////////////////error/////////////////////////////
    ///@notice errors thrown if input variables are not valid

    /// @dev thrown if address input is address 0
    error ZERO_ADDRESS();

